import React, { useEffect, useState } from 'react';
import { Card, Row, Col, Spinner } from 'react-bootstrap';

const VendorList = ({ location }) => {
  const [vendors, setVendors] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchVendors = async () => {
      if (!location) return;
      setLoading(true);

      try {
        const response = await fetch(`/api/vendors/nearby?lat=${location.lat}&lng=${location.lng}`);
        const data = await response.json();

        // Assuming API returns an array of vendor objects
        setVendors(data.vendors || []);
      } catch (error) {
        console.error('Error fetching vendors:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchVendors();
  }, [location]);

  if (loading) {
    return (
      <div className="text-center my-4">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </div>
    );
  }

  if (vendors.length === 0) {
    return <p className="text-center my-4">No vendors found near this location.</p>;
  }

  return (
    <Row className="g-4 my-4">
      {vendors.map((vendor) => (
        <Col xs={12} sm={6} md={4} lg={3} key={vendor._id}>
          <Card className="h-100 shadow-sm">
            <Card.Img variant="top" src={vendor.logoUrl || '/placeholder-logo.png'} alt={vendor.dispensaryName} />
            <Card.Body>
              <Card.Title className="text-truncate" title={vendor.dispensaryName}>
                {vendor.dispensaryName}
              </Card.Title>
              <Card.Text>
                <strong>Type:</strong> {vendor.dispensaryType}<br />
                <strong>Rating:</strong> ‚≠ê {vendor.rating || 'N/A'}<br />
                <strong>Min Order:</strong> ${vendor.minOrder?.toFixed(2) || '0.00'}
              </Card.Text>
              <Card.Text className="text-muted">
                <strong>Address:</strong><br />
                {vendor.businessAddress.street}, {vendor.businessAddress.city}, {vendor.businessAddress.state}, {vendor.businessAddress.zip}
              </Card.Text>
            </Card.Body>
            <Card.Footer className="text-center">
              <button className="btn btn-primary btn-sm">View Menu</button>
            </Card.Footer>
          </Card>
        </Col>
      ))}
    </Row>
  );
};

export default VendorList;
