import React from 'react';
import { useNavigate } from 'react-router-dom';
import ProductCard from './ProductCard';

const VendorCard = ({ vendor }) => {
  const navigate = useNavigate(); // Hook for navigation

  const handleNavigation = () => {
    // Navigate to vendor menu page
    navigate(`/vendor/${vendor._id}/menu`);
  };

  return (
    <div
      className="vendor-card shadow p-3 mb-4 rounded"
      style={{ border: '1px solid #ddd', cursor: 'pointer' }}
      onClick={handleNavigation} // Navigate when clicking the entire card
    >
      {/* Vendor Header */}
      <div className="d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center">
          {/* Dispensary Logo */}
          <img
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={vendor.dispensaryName}
            className="rounded"
            style={{
              width: '44px',
              height: '44px',
              objectFit: 'cover',
              marginRight: '10px',
              cursor: 'pointer',
            }}
            onClick={(e) => {
              e.stopPropagation(); // Prevent card click event
              handleNavigation();
            }}
          />

          {/* Dispensary Name */}
          <div>
            <h6
              className="mb-1"
              style={{ fontSize: '1rem', fontWeight: 'bold', cursor: 'pointer' }}
              onClick={(e) => {
                e.stopPropagation(); // Prevent card click event
                handleNavigation();
              }}
            >
              {vendor.dispensaryName}
            </h6>
            <p className="text-muted mb-0" style={{ fontSize: '0.85rem' }}>
              Minimum Order: ${vendor.minOrder || 'N/A'} | {vendor.milesAway || 'N/A'} miles away
            </p>
          </div>
        </div>

        {/* Menu Button */}
        <button
          className="btn btn-primary rounded-pill"
          onClick={(e) => {
            e.stopPropagation(); // Prevent card click event
            handleNavigation();
          }}
        >
          Menu
        </button>
      </div>

      {/* Products Preview */}
      <div className="products-preview mt-3">
        {vendor.products.length ? (
          <div className="d-flex overflow-auto" style={{ paddingBottom: '10px', scrollbarWidth: 'thin' }}>
            {vendor.products.slice(0, 10).map((product) => (
              <div key={product._id} className="me-2" style={{ minWidth: '200px', flex: '0 0 auto' }}>
                <ProductCard
                  product={product}
                  vendorName={vendor.dispensaryName}
                  logoUrl={vendor.logoUrl}
                  vendorId={vendor._id}
                />
              </div>
            ))}
          </div>
        ) : (
          <p className="text-muted">No products available</p>
        )}
      </div>
    </div>
  );
};

export default VendorCard;
