import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useJsApiLoader } from '@react-google-maps/api';

const AddressPage = () => {
  const [address, setAddress] = useState('');
  const navigate = useNavigate();
  const inputRef = useRef(null);

  const { isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['places'], // Include the places library
  });

  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      navigate('/nearby');
    }
  }, [navigate]);

  useEffect(() => {
    if (!isLoaded) return;

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['geocode'],
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place?.formatted_address) {
        setAddress(place.formatted_address);
        localStorage.setItem('userAddress', place.formatted_address);
        navigate('/nearby');
      }
    });
  }, [isLoaded, navigate]);

  if (loadError) {
    return <p>Error loading Google Maps API</p>;
  }

  if (!isLoaded) {
    return <p>Loading...</p>;
  }

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: '#0B0B0B' }}>
      <div
        className="card shadow"
        style={{
          width: '30rem',
          padding: '1.5rem',
          backgroundColor: '#F2ED6B',
          borderRadius: '1rem',
        }}
      >
        <div className="card-body text-center">
          <img
            src={require('../assets/images/logo.png')}
            alt="WeedHurry Logo"
            className="mb-4"
            style={{ maxWidth: '100%', height: 'auto' }}
          />
          <p className="card-text mb-4" style={{ color: '#333333', fontWeight: '500' }}>
            Find top brand cannabis products and delivery/pickup nearest you!
          </p>
          <div className="input-group">
            <input
              type="text"
              ref={inputRef}
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter your address"
              className="form-control rounded-pill px-3"
              style={{
                border: '1px solid #ced4da',
                paddingRight: '2.5rem',
                backgroundColor: '#ffffff',
              }}
            />
            <span
              className="input-group-text bg-transparent border-0"
              style={{
                position: 'absolute',
                right: '10px',
                top: '50%',
                transform: 'translateY(-50%)',
                cursor: 'pointer',
              }}
            >
              &gt;
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddressPage;
