import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import StoreDetails from './StoreDetails';
import VendorProducts from './VendorProducts';
import ProductList from './';
import { VendorProvider, VendorContext } from '../context/VendorContext';

const VendorStorePage = () => {
  const { vendorId } = useParams(); // Get vendorId from URL

  return (
    <VendorProvider vendorId={vendorId}>
      <VendorPageContent />
    </VendorProvider>
  );
};

const VendorPageContent = () => {
  const { vendor, products, error } = useContext(VendorContext);

  if (error) return <div>Error: {error}</div>;
  if (!vendor || !products) return <div>Loading...</div>;

  return (
    <div>
      <StoreDetails />
      <ProductList />
    </div>
  );
};

export default VendorStorePage;
