import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';

const VendorInfoCard = () => {
    const [vendor, setVendor] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        contactNumber: '',
        email: '',
    });
    const [alert, setAlert] = useState({ message: '', variant: '' });

    // Fetch vendor details on component mount
    useEffect(() => {
        const fetchVendorData = async () => {
            try {
                const response = await axios.get('/api/vendors/profile', {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                });
                setVendor(response.data.data);
            } catch (error) {
                console.error('Error fetching vendor details:', error);
                setAlert({ message: 'Failed to load vendor details.', variant: 'danger' });
            }
        };
        fetchVendorData();
    }, []);

    // Open modal and prefill form with current details
    const handleEdit = () => {
        setFormData({
            firstName: vendor.firstName || '',
            lastName: vendor.lastName || '',
            contactNumber: formatPhoneNumber(vendor.contactNumber || ''),
            email: vendor.email || '',
        });
        setShowModal(true);
    };

    // Format phone number as (###) ###-####
    const formatPhoneNumber = (value) => {
        const cleaned = value.replace(/\D/g, ''); // Remove all non-numeric characters
        const match = cleaned.match(/^(\d{0,3})(\d{0,3})(\d{0,4})$/);
        if (!match) return value;
        return [
            match[1] && `(${match[1]}`,
            match[2] && `${match[1] ? ') ' : ''}${match[2]}`,
            match[3] && `-${match[3]}`,
        ]
            .filter(Boolean)
            .join('');
    };

    // Validate email
    const validateEmail = (email) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    // Handle form input changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'contactNumber') {
            setFormData({ ...formData, contactNumber: formatPhoneNumber(value) });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    // Handle form submission
    const handleUpdate = async () => {
        // Validate phone number
        const cleanedPhoneNumber = formData.contactNumber.replace(/\D/g, '');
        if (cleanedPhoneNumber.length !== 10) {
            setAlert({ message: 'Phone number must be 10 digits.', variant: 'danger' });
            return;
        }

        // Validate email
        if (!validateEmail(formData.email)) {
            setAlert({ message: 'Invalid email address.', variant: 'danger' });
            return;
        }

        try {
            const response = await axios.put('/api/vendors/profile', {
                ...formData,
                contactNumber: cleanedPhoneNumber, // Save cleaned phone number
            }, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            setVendor(response.data.data); // Update vendor details in state
            setAlert({ message: 'Details updated successfully!', variant: 'success' });
            setShowModal(false); // Close modal
        } catch (error) {
            console.error('Error updating vendor details:', error);
            setAlert({ message: 'Failed to update details.', variant: 'danger' });
        }
    };

    return (
        <div>
            {/* Display alert if available */}
            {alert.message && <Alert variant={alert.variant}>{alert.message}</Alert>}

            {/* Vendor Details Card */}
            <Card className="shadow-sm mt-3" style={{ width: '24rem', margin: '0 auto' }}>
    <Card.Body>
        <div className="d-flex justify-content-between align-items-center">
            {/* Vendor Details */}
            <div className="me-3"> {/* Add margin-end to separate button */}
                <Card.Title>
                    {vendor.firstName || 'N/A'} {vendor.lastName || 'N/A'}
                </Card.Title>
                <Card.Text className="mb-0">
                    {formatPhoneNumber(vendor.contactNumber || '')} <br />
                    {vendor.email || 'N/A'}
                </Card.Text>
            </div>

            {/* Edit Button */}
            <div>
                <Button variant="btn" size="sm" onClick={handleEdit}>
                    Edit
                </Button>
            </div>
        </div>
    </Card.Body>
</Card>


            {/* Edit Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Edit Vendor Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Contact Number</Form.Label>
                            <Form.Control
                                type="text"
                                name="contactNumber"
                                value={formData.contactNumber}
                                onChange={handleChange}
                                maxLength="14" // Limits to (###) ###-####
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={handleUpdate}>
                        Update
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default VendorInfoCard;
