import React from 'react';
import { Modal, Button } from 'react-bootstrap';

const ProductDetails = ({ show, handleClose, product }) => {
  if (!product) return null;

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title><strong>{product.brand}</strong> | {product.category}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="text-center">
          {/* Product Image */}
          <img
            src={product.variation?.image || '/placeholder-image.png'}
            alt={product.name || 'Product Image'}
            className="img-fluid mb-3"
            style={{ maxHeight: '200px', objectFit: 'contain' }}
          />
        </div>
        {/* Product Info */}
        <h5 className="fw-bold mb-3">{product.name}</h5>
        <p className="text-muted">{product.description || ''}</p>
        <div className="mb-2 d-flex align-items-center">
            <strong>{product.variation?.strain || 'N/A'}</strong>
        </div>




        <div className="mb-2">
          <strong>Amount:</strong> {product.variation?.amount || 'N/A'}
        </div>
        <div className="mb-2">
          <strong>Price:</strong>{' '}
          {product.variation?.salePrice ? (
            <>
              <span style={{ color: '#FF0080', fontWeight: 'bold' }}>
                ${product.variation.salePrice.toFixed(2)}
              </span>{' '}
              <span style={{ textDecoration: 'line-through', color: 'gray' }}>
                ${product.variation.price.toFixed(2)}
              </span>
            </>
          ) : (
            <span>${product.variation?.price?.toFixed(2) || 'N/A'}</span>
          )}
        </div>
        <div className="mb-3">
          <strong>Strain:</strong> {product.variation?.strain || 'N/A'}
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default ProductDetails;
