import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import api from '../utils/api';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState(null);
    const [loading, setLoading] = useState(true);

    // Function to log in the user
    const login = async (vendorId) => {
        setAuth({ vendorId }); // Update auth state directly
    };

    const logout = async () => {
        try {
            // Use the api instance instead of raw axios
            await api.post('/auth/logout');
            setAuth(null);
            window.location.href = '/login';
        } catch (error) {
            console.error('Logout failed:', error);
            // Optionally still clear auth state on error
            setAuth(null);
            throw error; // Re-throw to let components handle the error
        }
    };

    // Function to check authentication status
    const checkAuthStatus = async () => {
        try {
            const response = await api.get('/auth/validate', {
                withCredentials: true
            });

            if (response.data?.success) {
                setAuth({ vendorId: response.data.vendorId });
            } else {
                await api.post('/auth/refresh-token');
                const validationResponse = await api.get('/auth/validate');
                if (validationResponse.data?.success) {
                    setAuth({ vendorId: validationResponse.data.vendorId });
                } else {
                    setAuth(null);
                }
            }
        } catch (error) {
            console.error('Auth check failed:', error);
            setAuth(null);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        checkAuthStatus();
    }, []);

    return (
        <AuthContext.Provider value={{ auth, login, logout, loading }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
