import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Image, Offcanvas } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeoAlt } from 'react-bootstrap-icons';

const CustomNavbar = () => {
  const [vendor, setVendor] = useState(null); // Holds vendor data
  const [storeLogo, setStoreLogo] = useState(null);
  const [showMenu, setShowMenu] = useState(false); // Controls the visibility of the slide-in menu
  const [parsedAddress, setParsedAddress] = useState({ street: '', city: '', stateAbbreviation: '' }); // User's address
  const navigate = useNavigate(); // For navigation

  useEffect(() => {
    // Fetch vendor details if logged in
    const fetchVendorData = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage
      if (token) {
        try {
          const response = await axios.get('/api/vendors/profile', {
            headers: { Authorization: `Bearer ${token}` },
          });
          const vendorData = response.data.data;
          setVendor(vendorData);
          setStoreLogo(vendorData.logoUrl);
        } catch (error) {
          console.error('Error fetching vendor data:', error);
        }
      }
    };

    // Parse user's saved address
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      const parts = savedAddress.split(',');
      setParsedAddress({
        street: parts[0]?.trim() || '',
        city: parts[1]?.trim() || '',
        stateAbbreviation: parts[2]?.trim().split(' ')[0] || '',
      });
    }

    fetchVendorData();
  }, []);

  const toggleMenu = () => setShowMenu((prev) => !prev); // Toggles menu visibility

  // Function to clear address and navigate home
  const clearAddressAndNavigate = () => {
    localStorage.removeItem('userAddress');
    navigate('/');
  };

  return (
    <>
      <Navbar style={{ backgroundColor: '#fff176' }} variant="light" expand="lg" className="px-3 navbar">
        {/* Left Side: Logo */}
        <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
          <Image
            src={require('../assets/images/miniLogo.jpg')}
            alt="logo"
            height="32"
            className="d-inline-block align-top rounded me-2"
          />
        </Navbar.Brand>

        {/* Middle: User Address */}
        <div className="d-flex justify-content-center flex-grow-1">
          <div
            className="d-flex align-items-center text-dark"
            style={{
              fontSize: '13px',
              fontWeight: 'bold',
              fontFamily: 'Poppins, sans-serif',
              marginLeft: '-24px',
              cursor: 'pointer',
            }}
            onClick={clearAddressAndNavigate} // Clears address on click
          >
            <GeoAlt className="me-2 text-dark" size={15} />
            <span style={{ fontWeight: '600', fontFamily: 'Poppins, sans-serif' }}>
              {parsedAddress.street || `${parsedAddress.city}, ${parsedAddress.stateAbbreviation}`}
            </span>
          </div>
        </div>

        {/* Right Side: Menu Icon */}
        <Nav>
          <div
            onClick={toggleMenu}
            className="text-dark p-0 d-flex align-items-center"
            style={{ cursor: 'pointer' }}
          >
            <i className="bi bi-list" style={{ fontSize: '1.5rem', color: 'black' }}></i>
          </div>
        </Nav>
      </Navbar>

      {/* Slide-In Menu */}
      <Offcanvas show={showMenu} onHide={toggleMenu} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Menu</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          {!vendor ? (
            // If no vendor is logged in
            <div>
              <Link to="/login" onClick={toggleMenu} className="d-block mb-3">
                Login
              </Link>
            </div>
          ) : (
            // If vendor is logged in
            <div>
              <div className="d-flex align-items-center mb-3">
                <Image
                  src={storeLogo || '/default-avatar.png'} // Replace with default avatar
                  alt="Store Logo"
                  roundedCircle
                  width="40"
                  height="40"
                  className="me-2"
                />
                <div>
                  <strong>{vendor.dispensaryName}</strong>
                  <div className="text-muted">
                    {vendor.firstName} {vendor.lastName?.charAt(0)}.
                  </div>
                </div>
              </div>
              <Link to="/account" onClick={toggleMenu} className="d-block mb-3">
                Account
              </Link>
              <Link to="/logout" onClick={toggleMenu} className="d-block mb-3">
                Logout
              </Link>
            </div>
          )}
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
};

export default CustomNavbar;
