/* global google */

import React, { useEffect, useRef } from 'react';
import { GoogleMap, useLoadScript } from '@react-google-maps/api';

const libraries = ['places'];

const containerStyle = {
  width: '100%',
  height: '400px',
};

const center = {
  lat: 34.0522, // Default to Los Angeles
  lng: -118.2437,
};

function MapComponent({ onLocationSelect }) {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyBKb82KnQEbd1OGJxY08DbvsmRcz33h48w',
    libraries: ['places'],
  });

  const mapRef = useRef(null);

  useEffect(() => {
    if (isLoaded && mapRef.current) {
      const map = mapRef.current;
      let marker;

      // Add a click listener to place an AdvancedMarkerElement
      map.addListener('click', (event) => {
        const position = {
          lat: event.latLng.lat(),
          lng: event.latLng.lng(),
        };

        // Remove the existing marker if any
        if (marker) marker.map = null;

        // Create a new AdvancedMarkerElement
        marker = new google.maps.marker.AdvancedMarkerElement({
          position,
          map,
        });

        if (onLocationSelect) {
          onLocationSelect(position);
        }
      });
    }
  }, [isLoaded, onLocationSelect]);

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading...</div>;

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={center}
      zoom={10}
      onLoad={(map) => (mapRef.current = map)}
    />
  );
}

export default MapComponent;
