import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext'; // Adjust path as necessary

const ProductCard = ({ product, vendorName, logoUrl }) => {
  const { addToCart } = useContext(CartContext);

  return (
    <div className="card product-card shadow-sm" style={{ width: '14rem', position: 'relative' }}>
      {/* SALE Banner */}
      {product.salePrice && (
        <div
          className="position-absolute top-0 start-0 w-100 text-center"
          style={{
            backgroundColor: '#FF0080',
            color: 'white',
            fontSize: '0.8rem',
            padding: '0.3rem 0',
            fontWeight: 'bold',
          }}
        >
          SALE
        </div>
      )}

      {/* Card Body */}
      <div className="card-body d-flex flex-column justify-content-between">
        {/* Product Info */}
        <div>
          {/* Category */}
          <p className="text-muted" style={{ fontSize: '0.8rem', marginBottom: '1rem' }}>
            {product.category}
          </p>
          {/* Product Image */}
          <div className="text-center mb-2">
            <img
              src={product.images[0] || '/placeholder-image.png'}
              alt={product.name}
              className="img-fluid"
              style={{ maxHeight: '120px', objectFit: 'contain' }}
            />
          </div>
          <div>
              {/* Brand and Name */}
            <h6 className="card-title" style={{ fontSize: '0.9rem' }}>
            <strong>{product.brand}</strong> | {product.name}
          </h6>
          </div>

          {/* Variations */}
          {product.variations && product.variations.length > 0 ? (
            product.variations.slice(0, 1).map((variation, index) => (
              <div key={index}>
                <p style={{ fontSize: '0.85rem', fontWeight: '500' }}>
                  Strain: {variation.strain} ({variation.classification})
                </p>
                <span className="badge border border-secondary text-secondary">
                  {variation.thcContent}% THC
                </span>
              </div>
            ))
          ) : (
            <p className="text-muted" style={{ fontSize: '0.85rem' }}>
              No variations available
            </p>
          )}
        </div>

        {/* Footer Section */}
        <div className="d-flex justify-content-between align-items-center mt-3">
          {/* Price Section */}
          <div>
            {product.salePrice ? (
              <>
                <span style={{ color: '#FF0080', fontWeight: 'bold' }}>
                  ${product.salePrice.toFixed(2)}
                </span>{' '}
                <span style={{ textDecoration: 'line-through', color: 'gray', fontSize: '0.85rem' }}>
                  ${product.price.toFixed(2)}
                </span>
              </>
            ) : (
              <span style={{ fontWeight: 'bold' }}>${product.price?.toFixed(2) || 'N/A'}</span>
            )}
          </div>

          {/* Add to Cart Button */}
          <button
            className="btn btn-primary rounded-circle"
            style={{
              width: '2.5rem',
              height: '2.5rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() =>
              addToCart({
                ...product,
                vendorName,
                logoUrl,
              })
            }
          >
            <i className="bi bi-plus-lg"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
