import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card, Spinner, Alert } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const { 
    calculateTotal, 
    cartItems, 
    incrementItem, 
    decrementItem, 
    removeFromCart, 
    clearCart, 
  } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    day: '',
    month: '',
    year: '',
  });

  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    zip: '',
  });

  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const navigate = useNavigate();

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setDeliveryAddress({ ...deliveryAddress, [name]: value });
  };

  const handlePlaceOrder = async () => {
    setLoading(true);
    setErrorMessage(''); // Clear any previous error messages

    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone) {
      alert('Please fill out all required fields.');
      setLoading(false);
      return;
    }
    if (!deliveryAddress.street || !deliveryAddress.city || !deliveryAddress.state || !deliveryAddress.zip) {
      alert('Please fill out all delivery address fields.');
      setLoading(false);
      return;
    }

    const birthdate = `${contactInfo.year}-${contactInfo.month.toString().padStart(2, '0')}-${contactInfo.day.toString().padStart(2, '0')}`;

    const orderDetails = {
      type: 'DELIVERY',
      order_source: 'ECOMMERCE',
      order_status: 'AWAITING_PROCESSING',
      delivery_address: deliveryAddress,
      items: cartItems.map((item) => ({
        size_id: item.productId,
        name: item.name,

        quantity: item.quantity,

      })),
    };
    // Create a summary of the cart items for the order confirmation page
    const cartItemsSummary = cartItems.map((item) => ({
        name: item.name,
        quantity: item.quantity,
        price: item.price.toFixed(2),
        category: item.category,
    }));

    const payload = {
      phone: contactInfo.phone,
      firstName: contactInfo.firstName,
      lastName: contactInfo.lastName,
      email: contactInfo.email,
      birthday: birthdate,
      totalAmount: estimatedTotal,
      vendorId: cartItems[0]?.vendorId, 
      orderDetails,
      cartItemsSummary,
    };

    try {
      const response = await fetch('http://localhost:5001/api/customer/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const result = await response.json();
        navigate(`/order-confirmation/${result.order._id}`, {
            state: {
              order: result.order,
              cartItemsSummary: payload.cartItemsSummary, // Pass the cart items summary
            },
          });

        clearCart();
      } else {
        const error = await response.json();
        alert(`Failed to place order: ${error.message}`);
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="place-order-section p-4 bg-light">
      {/* Delivery Address Section */}
      <div className="mb-4">
        <h4>Delivery Address</h4>
        <Form>
          <Form.Group controlId="street" className="mb-3">
            <Form.Label>Street Address</Form.Label>
            <Form.Control
              type="text"
              name="street"
              value={deliveryAddress.street}
              onChange={handleAddressChange}
              required
            />
          </Form.Group>
          <Row className="mb-3">
            <Col>
              <Form.Label>City</Form.Label>
              <Form.Control
                type="text"
                name="city"
                value={deliveryAddress.city}
                onChange={handleAddressChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>State</Form.Label>
              <Form.Control
                as="select"
                name="state"
                value={deliveryAddress.state}
                onChange={handleAddressChange}
                required
              >
                <option value="">Select</option>
                {['CA', 'CO', 'NY', 'FL', 'NV', 'OR', 'NJ'].map((state) => (
                  <option key={state} value={state}>{state}</option>
                ))}
              </Form.Control>
            </Col>
            <Col>
              <Form.Label>ZIP Code</Form.Label>
              <Form.Control
                type="text"
                name="zip"
                value={deliveryAddress.zip}
                onChange={handleAddressChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Contact Information Section */}
      <div className="mb-4">
        <h4>Contact Information</h4>
        <Form>
          <Row className="mb-3">
            <Col>
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={contactInfo.firstName}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={contactInfo.lastName}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Label>Birthdate</Form.Label>
              <Row>
                <Col>
                  <Form.Control
                    as="select"
                    name="year"
                    value={contactInfo.year}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Year</option>
                    {[...Array(100).keys()].map((i) => {
                      const year = new Date().getFullYear() - i;
                      return <option key={year} value={year}>{year}</option>;
                    })}
                  </Form.Control>
                </Col>
                <Col>
                  <Form.Control
                    as="select"
                    name="month"
                    value={contactInfo.month}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Month</option>
                    {Array.from({ length: 12 }, (_, i) => i + 1).map((month) => (
                      <option key={month} value={month}>{month}</option>
                    ))}
                  </Form.Control>
                </Col>
                <Col>
                  <Form.Control
                    as="select"
                    name="day"
                    value={contactInfo.day}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Day</option>
                    {Array.from({ length: 31 }, (_, i) => i + 1).map((day) => (
                      <option key={day} value={day}>{day}</option>
                    ))}
                  </Form.Control>
                </Col>
              </Row>
            </Col>
          </Row>
          <Row className="mb-3">
            <Col>
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="email"
                value={contactInfo.email}
                onChange={handleInputChange}
                required
              />
            </Col>
            <Col>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                name="phone"
                value={contactInfo.phone}
                onChange={handleInputChange}
                required
              />
            </Col>
          </Row>
        </Form>
      </div>

      {/* Order Summary Section */}
      <div className="mb-4">
        <h4>Order Summary</h4>
        <Card className="cart-summary-card mb-1" style={{
          border: 'none',
          backgroundColor: '#f8f9fa',
          margin: '20px',
          borderRadius: '8px',
        }}>
          <Card.Body>
            {cartItems.length > 0 ? (
              <>
                {cartItems.map((item) => (
                  <div key={item.productId} className="d-flex justify-content-between align-items-center mb-3">
                    <div className="d-flex align-items-center">
                      <img
                        src={item.primaryImage}
                        alt={item.name}
                        className="img-fluid"
                        style={{ width: '40px', height: '40px', marginRight: '10px' }}
                      />
                      <div>
                        <div>{item.name}</div>
                        <div>${item.price.toFixed(2)}</div>
                      </div>
                    </div>
                    <div className="d-flex align-items-center">
                      <Button variant="outline-secondary" size="sm" onClick={() => decrementItem(item.productId)}>-</Button>
                      <span className="mx-2">{item.quantity}</span>
                      <Button variant="outline-secondary" size="sm" onClick={() => incrementItem(item.productId)}>+</Button>
                      <Button variant="link" size="sm" className="text-danger ms-2" onClick={() => removeFromCart(item.productId)}>Remove</Button>
                    </div>
                  </div>
                ))}
                <div className="d-flex justify-content-between mt-3">
                  <strong>Subtotal</strong>
                  <span>${subtotal.toFixed(2)}</span>
                </div>
                <div className="d-flex justify-content-between mt-3">
                  <strong>Estimated Total w/ Taxes</strong>
                  <span>${estimatedTotal.toFixed(2)}</span>
                </div>
              </>
            ) : (
              <p className="text-center mb-1">Your cart is empty.</p>
            )}
          </Card.Body>
        </Card>
      </div>

      {/* Place Order Button */}
      <Button variant="primary" className="w-100 mb-3" onClick={handlePlaceOrder}>Place Order</Button>
      <p className="text-muted text-center">Please note, vendor will notify you of delivery ETA shortly after placing order.</p>
    </div>
  );
};

export default TestPlaceOrder;
