import React, { useState } from 'react';
import './SlideInCard.css'; // Import CSS for styling

const SlideInCard = () => {
  const [cardPosition, setCardPosition] = useState('middle'); // 'middle' or 'full'
  const [dragStartY, setDragStartY] = useState(null);

  const handleDragStart = (e) => {
    const startY = e.touches ? e.touches[0].clientY : e.clientY;
    setDragStartY(startY);
  };

  const handleDragMove = (e) => {
    if (!dragStartY) return;

    const currentY = e.touches ? e.touches[0].clientY : e.clientY;
    const deltaY = currentY - dragStartY;

    if (deltaY < -50) {
      setCardPosition('full'); // Dragged up
    } else if (deltaY > 50) {
      setCardPosition('middle'); // Dragged down
    }
  };

  const handleDragEnd = () => {
    setDragStartY(null); // Reset drag state
  };

  return (
    <div>
      {/* Background Content */}
      <div style={{ height: '100vh', backgroundColor: '#f5f5f5' }}>
        <h1 style={{ textAlign: 'center', paddingTop: '20px' }}>Background Content</h1>
      </div>

      {/* Sliding Card */}
      <div
        className={`slide-in-card ${cardPosition}`}
        style={{
          transform: `translateY(${cardPosition === 'full' ? '0%' : '50%'})`,
          transition: dragStartY ? 'none' : 'transform 0.3s ease-in-out',
        }}
        onMouseDown={handleDragStart}
        onMouseMove={handleDragMove}
        onMouseUp={handleDragEnd}
        onTouchStart={handleDragStart}
        onTouchMove={handleDragMove}
        onTouchEnd={handleDragEnd}
      >
        {/* Drag Handle */}
        <div className="drag-handle"></div>

        {/* Card Content */}
        <div className="card-content">
          <h2>Slide-In Card</h2>
          <p>This is some content inside the card. Drag the handle to move the card.</p>
        </div>
      </div>
    </div>
  );
};

export default SlideInCard;
