import React, { useState } from 'react';
import { X, Plus, Image as ImageIcon, AlertCircle } from 'lucide-react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Alert, AlertDescription } from "@/components/ui/alert";

const CreateProductModal = ({ isOpen, onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    brand: '',
    name: '',
    category: 'Flower',
    description: '',
    variations: [
      {
        amount: '',
        strain: '',
        thcContent: '',
        price: '',
        salePrice: '',
        stock: '',
        image: null
      }
    ]
  });

  const [errors, setErrors] = useState({});
  const [imageUrls, setImageUrls] = useState([]);

  const categories = [
    'Flower',
    'Pre-roll',
    'Vape',
    'Edible',
    'Concentrate',
    'Tincture',
    'Gear'
  ];

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleVariationChange = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      variations: prev.variations.map((v, i) => 
        i === index ? { ...v, [field]: value } : v
      )
    }));
    // Clear variation error
    if (errors[`variation-${index}-${field}`]) {
      setErrors(prev => ({
        ...prev,
        [`variation-${index}-${field}`]: ''
      }));
    }
  };

  const handleImageUpload = (index, e) => {
    const file = e.target.files[0];
    if (file) {
      const url = URL.createObjectURL(file);
      setImageUrls(prev => {
        const newUrls = [...prev];
        newUrls[index] = url;
        return newUrls;
      });
      handleVariationChange(index, 'image', file);
    }
  };

  const addVariation = () => {
    setFormData(prev => ({
      ...prev,
      variations: [...prev.variations, {
        amount: '',
        strain: '',
        thcContent: '',
        price: '',
        salePrice: '',
        stock: '',
        image: null
      }]
    }));
  };

  const removeVariation = (index) => {
    setFormData(prev => ({
      ...prev,
      variations: prev.variations.filter((_, i) => i !== index)
    }));
    setImageUrls(prev => prev.filter((_, i) => i !== index));
  };

  const validateForm = () => {
    const newErrors = {};

    // Validate main fields
    if (!formData.brand.trim()) newErrors.brand = 'Brand is required';
    if (!formData.name.trim()) newErrors.name = 'Product name is required';
    if (!formData.category) newErrors.category = 'Category is required';

    // Validate variations
    formData.variations.forEach((variation, index) => {
      if (!variation.amount && formData.category !== 'Gear') {
        newErrors[`variation-${index}-amount`] = 'Amount is required';
      }
      if (!variation.strain && formData.category !== 'Gear') {
        newErrors[`variation-${index}-strain`] = 'Strain is required';
      }
      if (!variation.price || variation.price <= 0) {
        newErrors[`variation-${index}-price`] = 'Valid price is required';
      }
      if (variation.salePrice && Number(variation.salePrice) >= Number(variation.price)) {
        newErrors[`variation-${index}-salePrice`] = 'Sale price must be less than regular price';
      }
      if (!variation.stock || variation.stock < 0) {
        newErrors[`variation-${index}-stock`] = 'Valid stock is required';
      }
      if (!variation.image) {
        newErrors[`variation-${index}-image`] = 'Image is required';
      }
    });

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      onSubmit(formData);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl font-bold">Add New Product</DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="block text-sm font-medium">Brand</label>
              <input
                type="text"
                name="brand"
                value={formData.brand}
                onChange={handleInputChange}
                className={`w-full p-2 border rounded-lg ${errors.brand ? 'border-red-500' : 'border-gray-300'}`}
              />
              {errors.brand && (
                <p className="text-red-500 text-xs">{errors.brand}</p>
              )}
            </div>

            <div className="space-y-2">
              <label className="block text-sm font-medium">Product Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                className={`w-full p-2 border rounded-lg ${errors.name ? 'border-red-500' : 'border-gray-300'}`}
              />
              {errors.name && (
                <p className="text-red-500 text-xs">{errors.name}</p>
              )}
            </div>

            <div className="space-y-2">
              <label className="block text-sm font-medium">Category</label>
              <select
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                className="w-full p-2 border border-gray-300 rounded-lg"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>

            <div className="space-y-2 md:col-span-2">
              <label className="block text-sm font-medium">Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                rows={3}
                className="w-full p-2 border border-gray-300 rounded-lg"
              />
            </div>
          </div>

          <div className="border-t pt-6">
            <h3 className="text-lg font-semibold mb-4">Product Variations</h3>
            {formData.variations.map((variation, index) => (
              <div key={index} className="mb-8 p-4 bg-gray-50 rounded-lg">
                <div className="flex justify-between items-center mb-4">
                  <h4 className="text-md font-medium">Variation #{index + 1}</h4>
                  {index > 0 && (
                    <button
                      type="button"
                      onClick={() => removeVariation(index)}
                      className="text-red-500 hover:text-red-700"
                    >
                      <X className="h-5 w-5" />
                    </button>
                  )}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <label className="block text-sm font-medium">Amount</label>
                    <input
                      type="text"
                      value={variation.amount}
                      onChange={(e) => handleVariationChange(index, 'amount', e.target.value)}
                      className={`w-full p-2 border rounded-lg ${
                        errors[`variation-${index}-amount`] ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors[`variation-${index}-amount`] && (
                      <p className="text-red-500 text-xs">{errors[`variation-${index}-amount`]}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm font-medium">Strain</label>
                    <input
                      type="text"
                      value={variation.strain}
                      onChange={(e) => handleVariationChange(index, 'strain', e.target.value)}
                      className={`w-full p-2 border rounded-lg ${
                        errors[`variation-${index}-strain`] ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors[`variation-${index}-strain`] && (
                      <p className="text-red-500 text-xs">{errors[`variation-${index}-strain`]}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm font-medium">THC Content (%)</label>
                    <input
                      type="number"
                      step="0.1"
                      value={variation.thcContent}
                      onChange={(e) => handleVariationChange(index, 'thcContent', e.target.value)}
                      className="w-full p-2 border border-gray-300 rounded-lg"
                    />
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm font-medium">Price ($)</label>
                    <input
                      type="number"
                      step="0.01"
                      value={variation.price}
                      onChange={(e) => handleVariationChange(index, 'price', e.target.value)}
                      className={`w-full p-2 border rounded-lg ${
                        errors[`variation-${index}-price`] ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors[`variation-${index}-price`] && (
                      <p className="text-red-500 text-xs">{errors[`variation-${index}-price`]}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm font-medium">Sale Price ($)</label>
                    <input
                      type="number"
                      step="0.01"
                      value={variation.salePrice}
                      onChange={(e) => handleVariationChange(index, 'salePrice', e.target.value)}
                      className={`w-full p-2 border rounded-lg ${
                        errors[`variation-${index}-salePrice`] ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors[`variation-${index}-salePrice`] && (
                      <p className="text-red-500 text-xs">{errors[`variation-${index}-salePrice`]}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <label className="block text-sm font-medium">Stock</label>
                    <input
                      type="number"
                      value={variation.stock}
                      onChange={(e) => handleVariationChange(index, 'stock', e.target.value)}
                      className={`w-full p-2 border rounded-lg ${
                        errors[`variation-${index}-stock`] ? 'border-red-500' : 'border-gray-300'
                      }`}
                    />
                    {errors[`variation-${index}-stock`] && (
                      <p className="text-red-500 text-xs">{errors[`variation-${index}-stock`]}</p>
                    )}
                  </div>

                  <div className="space-y-2 md:col-span-2">
                    <label className="block text-sm font-medium">Image</label>
                    <div className="flex items-center space-x-4">
                      {imageUrls[index] ? (
                        <div className="relative w-32 h-32">
                          <img
                            src={imageUrls[index]}
                            alt={`Variation ${index + 1}`}
                            className="w-full h-full object-cover rounded-lg"
                          />
                          <button
                            type="button"
                            onClick={() => {
                              setImageUrls(prev => {
                                const newUrls = [...prev];
                                newUrls[index] = null;
                                return newUrls;
                              });
                              handleVariationChange(index, 'image', null);
                            }}
                            className="absolute top-1 right-1 bg-red-500 text-white rounded-full p-1 hover:bg-red-600"
                          >
                            <X className="h-4 w-4" />
                          </button>
                        </div>
                      ) : (
                        <div className="relative">
                          <input
                            type="file"
                            accept="image/*"
                            onChange={(e) => handleImageUpload(index, e)}
                            className="hidden"
                            id={`image-upload-${index}`}
                          />
                          <label
                            htmlFor={`image-upload-${index}`}
                            className="flex flex-col items-center justify-center w-32 h-32 border-2 border-dashed border-gray-300 rounded-lg cursor-pointer hover:border-gray-400"
                          >
                            <ImageIcon className="w-8 h-8 text-gray-400" />
                            <span className="mt-2 text-sm text-gray-500">Upload Image</span>
                          </label>
                        </div>
                      )}
                      {errors[`variation-${index}-image`] && (
                        <p className="text-red-500 text-xs">{errors[`variation-${index}-image`]}</p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}

            <button
              type="button"
              onClick={addVariation}
              className="flex items-center justify-center w-full p-3 border-2 border-dashed border-gray-300 rounded-lg text-gray-600 hover:border-gray-400 hover:text-gray-700 transition-colors mt-4"
            >
              <Plus className="w-5 h-5 mr-2" />
              Add Another Variation
            </button>
          </div>

          {Object.keys(errors).length > 0 && (
            <Alert variant="destructive" className="mt-4">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                Please fix the errors above before submitting.
              </AlertDescription>
            </Alert>
          )}

          <div className="flex justify-end space-x-4 mt-6 border-t pt-4">
            <button
              type="button"
              onClick={onClose}
              className="px-6 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              Create Product
            </button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
};

export default CreateProductModal;
