import React from 'react';
import { Card, Row, Col, Image } from 'react-bootstrap';
import VendorStatusDropdown from './VendorStatusDropdown';

const VendorDetails = ({ vendor }) => {
  if (!vendor || !vendor.businessHours) {
    return <p>Loading vendor details...</p>; // Handle uninitialized vendor object
  }

  return (
    <Card
      className="vendor-details p-3"
      style={{
        backgroundColor: '#FFF176', // Light yellow background
        border: 'none', // Remove card border
        borderRadius: '8px', // Optional: Rounded corners
      }}
    >
      <Row className="align-items-center">
        {/* Vendor Details */}
        <Col xs={12}>
          <div className="d-flex align-items-center justify-content-between">
            {/* Vendor Logo and Name */}
            <div className="d-flex align-items-center">
              {/* Vendor Logo */}
              <Image
                src={vendor.logoUrl || '/placeholder-logo.png'}
                alt={`${vendor.dispensaryName} logo`}
                style={{
                  width: '40px',
                  height: '45px',
                  objectFit: 'cover', // Ensure the image fills the circle without distortion
                  borderRadius: '50%', // Make it circular
                  border: '2px solid #ddd', // Optional: Add a border
                }}
              />

              {/* Vendor Name */}
              <h6
                style={{
                  fontWeight: 'bold',
                  margin: '0 0 0 10px', // Add left margin to separate logo and name
                  fontSize: '1rem',
                  color: '#333', // Optional: Add a darker text color for contrast
                }}
              >
                {vendor.dispensaryName}
              </h6>
            </div>

            {/* Vendor Status Dropdown */}
            <VendorStatusDropdown vendor={vendor} />
          </div>
        </Col>
      </Row>
    </Card>
  );
};

export default VendorDetails;
