import React, { useState } from 'react';
import { Dropdown, Form, Button } from 'react-bootstrap';

const SearchDropdown = ({ onSearch, onFilter }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    sativa: false,
    hybrid: false,
    indica: false,
  });

  const handleButtonToggle = (classification) => {
    setFilters((prevFilters) => ({
      ...prevFilters,
      [classification]: !prevFilters[classification],
    }));
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value);
  };

  const handleApplyFilters = () => {
    onFilter(filters);
  };

  return (
    <Dropdown
      style={{
        position: 'relative', // Ensure it creates its own stacking context
        zIndex: 1050, // High z-index to appear on top
      }}
    >
      <Dropdown.Toggle
        variant="outline-secondary"
        style={{
          borderRadius: '12px',
        }}
      >
        <i className="bi bi-search"></i>
      </Dropdown.Toggle>
      <Dropdown.Menu
        className="p-3"
        style={{
          minWidth: '300px',
          position: 'absolute',
          boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)', // Add subtle shadow
          border: '1px solid #ddd',
        }}
      >
        {/* Search Input */}
        <Form.Group className="mb-3">
          <Form.Control
            type="text"
            placeholder="Search by brand or product name"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </Form.Group>

        {/* Classification Filter Buttons */}
        <div className="d-flex justify-content-between mb-3">
          <Button
            variant={filters.sativa ? 'primary' : 'outline-primary'}
            className="flex-grow-1 mx-1"
            style={{
              padding: '0.25rem 0.5rem', // Decrease padding
              fontSize: '0.8rem', // Smaller font size
              lineHeight: '1.2', // Adjust line height for slim appearance
              borderRadius: '8px', // Keep slight rounding
            }}
            onClick={() => handleButtonToggle('sativa')}
          >
            Sativa
          </Button>
          <Button
            variant={filters.hybrid ? 'primary' : 'outline-primary'}
            className="flex-grow-1 mx-1"
            style={{
              padding: '0.25rem 0.5rem', // Decrease padding
              fontSize: '0.8rem', // Smaller font size
              lineHeight: '1.2', // Adjust line height for slim appearance
              borderRadius: '8px', // Keep slight rounding
            }}
            onClick={() => handleButtonToggle('hybrid')}
          >
            Hybrid
          </Button>
          <Button
            variant={filters.indica ? 'primary' : 'outline-primary'}
            className="flex-grow-1 mx-1"
            style={{
              padding: '0.25rem 0.5rem', // Decrease padding
              fontSize: '0.8rem', // Smaller font size
              lineHeight: '1.2', // Adjust line height for slim appearance
              borderRadius: '8px', // Keep slight rounding
            }}
            onClick={() => handleButtonToggle('indica')}
          >
            Indica
          </Button>
        </div>

        {/* Apply Filters Button */}
        <Button
          variant="primary"
          className="mt-3 w-100"
          onClick={handleApplyFilters}
        >
          Apply Filters
        </Button>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default SearchDropdown;
