import React, { useState, useEffect } from 'react';
import { Tabs, Tab, Button } from 'react-bootstrap';
import RecentOrders from './RecentOrders';
import InventoryComponent from './InventoryComponent';
import LogoutButton from './LogoutButton';
import StoreSettingsSidebar from './StoreSettingsSidebar';
import StoreNoticeComponent from './StoreNoticeComponent';
import UpdateDeliveryZoneComponent from './UpdateDeliveryZoneComponent';
import BusinessHoursComponent from './BusinessHoursComponent';
import MinOrderComponent from './MinOrderComponent';
import AccountPage from './AccountPage';
import { useLoadScript } from '@react-google-maps/api';

const libraries = ['places'];

const defaultHours = {
  monday: '',
  tuesday: '',
  wednesday: '',
  thursday: '',
  friday: '',
  saturday: '',
  sunday: '',
};

const generateTimeSlots = () => {
  const slots = [];
  let time = 6 * 60; // Start at 6:00 AM in minutes
  const end = 24 * 60; // End at midnight (24:00)

  while (time < end) {
    const hours = Math.floor(time / 60); // Convert minutes to hours
    const label = `${hours % 12 || 12}:00 ${hours < 12 ? 'AM' : 'PM'}`; // Format as AM/PM
    slots.push(label);
    time += 60; // Increment by 60 minutes for hourly slots
  }
  return slots;
};

const VendorDashboard = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'AIzaSyBKb82KnQEbd1OGJxY08DbvsmRcz33h48w',
    libraries,
  });

  const [initialNotice, setInitialNotice] = useState('');
  const [initialMinOrder, setInitialMinOrder] = useState(0);
  const [initialHours, setInitialHours] = useState(defaultHours); // Using defaultHours
  const [showSettings, setShowSettings] = useState(false); // State for showing the sidebar
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null); // To manage error states

  useEffect(() => {
    const fetchVendorData = async () => {
      setLoading(true); // Start loading
      try {
        const token = localStorage.getItem('token');
        if (!token) throw new Error('No token found in localStorage');
        const response = await fetch('http://localhost:5001/api/vendors/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!response.ok) throw new Error('Failed to fetch vendor profile');
        const data = await response.json();
        setInitialNotice(data.data.storeNotice || '');
        setInitialMinOrder(data.data.minOrder || 0);
        setInitialHours(data.data.businessHours || defaultHours);
        setError(null); // Clear any previous errors
      } catch (error) {
        console.error('Error fetching vendor data:', error);
        setError(error.message);
      } finally {
        setLoading(false); // Stop loading
      }
    };

    fetchVendorData();
  }, []);

  const handleUpdateNotice = async (newNotice) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/vendors/store-notice', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ storeNotice: newNotice }),
      });
      if (response.ok) {
        alert('Store notice updated successfully!');
        setInitialNotice(newNotice);
      } else {
        alert('Failed to update store notice.');
      }
    } catch (error) {
      console.error('Error updating store notice:', error);
      alert('An error occurred.');
    }
  };

  const handleUpdateMinOrder = async (newMinOrder) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/vendors/min-order', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ minOrder: newMinOrder }),
      });
      if (response.ok) {
        alert('Minimum order updated successfully!');
        setInitialMinOrder(newMinOrder);
      } else {
        throw new Error('Failed to update minimum order');
      }
    } catch (error) {
      console.error('Error updating minimum order:', error);
      alert('An error occurred.');
    }
  };

  const handleUpdateHours = async (updatedHours) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5001/api/vendors/business-hours', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ businessHours: updatedHours }),
      });
      if (response.ok) {
        alert('Business hours updated successfully!');
        setInitialHours(updatedHours);
      } else {
        throw new Error('Failed to update business hours');
      }
    } catch (error) {
      console.error('Error updating business hours:', error);
      alert('An error occurred while updating business hours.');
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading map...</div>;

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center m-4">
        <h2 className="flex-grow-1 text-center">Vendor Dashboard</h2>
        <Button variant="secondary" onClick={() => setShowSettings(true)}>
          Store Settings
        </Button>
        <LogoutButton />
      </div>
      {loading && <div>Loading...</div>}
      {error && <div className="alert alert-danger">{error}</div>}
      <Tabs defaultActiveKey="orders" id="vendor-dashboard-tabs" className="mb-3">
        <Tab eventKey="orders" title="Orders">
          <RecentOrders />
        </Tab>
        <Tab eventKey="inventory" title="Inventory">
          <InventoryComponent />
        </Tab>
        <Tab eventKey="profile" title="Profile">
          <AccountPage />
        </Tab>
      </Tabs>

      {/* Store Settings Slide-In */}
      <StoreSettingsSidebar show={showSettings} handleClose={() => setShowSettings(false)}>
        <StoreNoticeComponent initialNotice={initialNotice} onUpdate={handleUpdateNotice} />
        <UpdateDeliveryZoneComponent isLoaded={isLoaded} loadError={loadError} />
        <BusinessHoursComponent
          initialHours={initialHours}
          onUpdate={handleUpdateHours}
          availableTimes={generateTimeSlots()}
        />
        <MinOrderComponent initialMinOrder={initialMinOrder} onUpdate={handleUpdateMinOrder} />
      </StoreSettingsSidebar>
    </div>
  );
};

export default VendorDashboard;
