import React from 'react';
import ProductCard from './ProductCard';
import { useNavigate, useLocation } from 'react-router-dom';

const VendorCard = ({ vendor }) => {
  const navigate = useNavigate(); // Hook for navigation
  const location = useLocation(); // Access the current location
  const referrer = location.pathname.includes('NearbyPickup') ? 'NearbyPickup' : 'NearbyDelivery';

  const handleViewMenu = () => {
    // Determine the referrer dynamically based on the current page context
    const referrer = location.pathname.includes('NearbyPickup') ? 'NearbyPickup' : 'NearbyDelivery';

    // Navigate to vendor menu page with referrer state
    navigate(`/vendor/${vendor._id}/menu`, { state: { referrer } });
};


  return (
    <div
      className="vendor-card p-3 mb-0"
      style={{ border: '1px solid #ddd', cursor: 'pointer' }}
    >
      {/* Vendor Header */}
      <div className="d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center">
          {/* Dispensary Logo */}
          <img
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={vendor.dispensaryName}
            className="rounded"
            onClick={handleViewMenu}
            style={{
              width: '44px',
              height: '44px',
              objectFit: 'cover',
              marginRight: '10px',
              cursor: 'pointer',
            }}
          />

          {/* Dispensary Name */}
          <div>
            <h6 className="mb-1" onClick={handleViewMenu} style={{ fontSize: '1rem', fontWeight: 'bold' }}>
              {vendor.dispensaryName}
            </h6>
            <p className="text-muted mb-0" style={{ fontSize: '0.85rem' }}>
              {vendor.minOrder && vendor.minOrder > 0 ? `$${vendor.minOrder}` : 'No'} Min. | {vendor.milesAway || 'N/A'} miles away
            </p>
          </div>
        </div>

        {/* Menu Button */}
       <button
  className="btn rounded-pill"
  onClick={handleViewMenu}
  style={{
    backgroundColor: '#005FEB', // Custom green background
    color: '#FFFFFF',          // White text
    border: 'none',            // No border for a modern look
    padding: '10px 20px',      // Custom padding for better size
    fontSize: '1rem',          // Adjust font size
    cursor: 'pointer',         // Pointer cursor for interactivity
    transition: 'background-color 0.3s ease', // Smooth hover effect
  }}
  onMouseEnter={(e) => (e.target.style.backgroundColor = '#024DBC')} // Hover effect
  onMouseLeave={(e) => (e.target.style.backgroundColor = '#005FEB')} // Reset hover
>
  Menu
</button>

      </div>

      {/* Filtered Products Preview */}
      <div className="products-preview mt-3">
        {vendor.products && vendor.products.length ? (
          <div className="d-flex overflow-auto" style={{ paddingBottom: '10px', scrollbarWidth: 'thin' }}>
            {vendor.products.map((product) => (
  <div
    key={`${product._id}-${product.strain}-${product.amount}`} // Ensure unique key
    className="me-2"
    style={{ minWidth: '200px', flex: '0 0 auto' }}
  >
    <ProductCard
      product={product}
      vendorName={vendor.dispensaryName}
      logoUrl={vendor.logoUrl}
      vendorId={vendor._id}
    />
  </div>
))}

          </div>
        ) : (
          <p className="text-muted">No matching products available</p>
        )}
      </div>
    </div>
  );
};

export default VendorCard;
