import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import VendorCard from './VendorCard';
import api from '../api/api';

const VendorMap = () => {
  const [vendors, setVendors] = useState([]);
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [mapCenter, setMapCenter] = useState({
    lat: 34.0522,
    lng: -118.2437
  });
  const [userLocation, setUserLocation] = useState(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);

  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  useEffect(() => {
    const getCurrentLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const userPos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude
            };
            setUserLocation(userPos);
            setMapCenter(userPos);
          },
          (error) => {
            console.error('Geolocation error:', error);
          }
        );
      }
    };

    const fetchVendors = async () => {
      try {
        setLoading(true);
        console.log('Fetching vendors from:', `${api.defaults.baseURL}/api/map/map-vendors`);
        const response = await api.get('/api/map/map-vendors');

        if (response.data.success) {
          const transformedVendors = response.data.vendors.map(vendor => {
            console.log('Raw vendor coordinates:', vendor.storefrontAddress?.coordinates);

            const transformed = {
              ...vendor,
              coordinates: vendor.storefrontAddress?.coordinates ? {
                lat: parseFloat(vendor.storefrontAddress.coordinates[1]),
                lng: parseFloat(vendor.storefrontAddress.coordinates[0])
              } : null
            };

            console.log('Transformed vendor coordinates:', transformed.coordinates);
            return transformed;
          });

          const filteredVendors = transformedVendors.filter(vendor => {
            const isValid = vendor.coordinates && 
                          !isNaN(vendor.coordinates.lat) && 
                          !isNaN(vendor.coordinates.lng);
            if (!isValid) {
              console.log('Filtered out vendor:', vendor.dispensaryName, vendor.coordinates);
            }
            return isValid;
          });

          console.log('Final vendors:', filteredVendors);
          setVendors(filteredVendors);
        }
      } catch (err) {
        console.error('Error fetching vendors:', err);
        console.error('Error details:', err.response?.data || 'No response data');
        setError(`Failed to load vendors: ${err.message}`);
      } finally {
        setLoading(false);
      }
    };

    getCurrentLocation();
    fetchVendors();
  }, []);

  const handleMapLoad = (map) => {
    console.log('Map loaded');
    setIsMapLoaded(true);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl">Loading vendors...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="flex h-screen">
      {/* Map Section */}
      <div className="w-1/2 h-full relative">
        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={mapStyles}
            zoom={12}
            center={mapCenter}
            options={{
              zoomControl: true,
              streetViewControl: false,
              mapTypeControl: false,
              fullscreenControl: false,
            }}
            onLoad={handleMapLoad}
          >
            {isMapLoaded && userLocation && (
              <Marker
                position={userLocation}
                icon={{
                  url: "data:image/svg+xml,%3Csvg width='24' height='24' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='12' cy='12' r='7' fill='%234285F4' stroke='white' stroke-width='2'/%3E%3C/svg%3E",
                  scaledSize: new window.google.maps.Size(24, 24)
                }}
              />
            )}

            {isMapLoaded && vendors.map(vendor => {
              console.log('Rendering marker for vendor:', vendor.dispensaryName, vendor.coordinates);
              return (
                <Marker
                  key={vendor._id}
                  position={vendor.coordinates}
                  onClick={() => {
                    console.log('Marker clicked:', vendor.dispensaryName);
                    setSelectedVendor(vendor);
                  }}
                  icon={{
                    url: vendor.logoUrl || '/placeholder-logo.png',
                    scaledSize: new window.google.maps.Size(40, 40)
                  }}
                />
              );
            })}

            {selectedVendor && selectedVendor.coordinates && (
              <InfoWindow
                position={selectedVendor.coordinates}
                onCloseClick={() => setSelectedVendor(null)}
              >
                <div className="p-2">
                  <h3 className="font-semibold text-lg">{selectedVendor.dispensaryName}</h3>
                  <p className="text-sm text-gray-600">{selectedVendor.storefrontAddress.formatted}</p>
                  <p className="text-sm">{selectedVendor.dispensaryType}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>

      {/* Vendor Cards Section */}
      <div className="w-1/2 h-full overflow-y-auto bg-gray-50 p-4">
        <h2 className="text-2xl font-semibold mb-4">Nearby Dispensaries ({vendors.length})</h2>
        <div className="space-y-4">
          {vendors.map(vendor => (
            <VendorCard
              key={vendor._id}
              vendor={vendor}
              orderType={vendor.dispensaryType.toLowerCase()}
              isSelected={selectedVendor?._id === vendor._id}
              onClick={() => {
                console.log('Card clicked:', vendor.dispensaryName, vendor.coordinates);
                setSelectedVendor(vendor);
                setMapCenter(vendor.coordinates);
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default VendorMap;
