import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import { useNavigate, useLocation } from 'react-router-dom';
import DeliveryBy from './DeliveryBy';

const Cart = () => {
  const {
    cartItems,
    removeFromCart,
    incrementItem,
    decrementItem,
    calculateTotal,
    getItemCount,
  } = useContext(CartContext);
  const [isCartOpen, setCartOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  // Toggle the cart visibility
  const toggleCart = () => setCartOpen(!isCartOpen);

  // Check if the user is on the checkout page
  const isCheckoutPage = location.pathname === '/checkout';

  return (
    <>
      {/* Conditionally Render Cart Icon */}
      {!isCheckoutPage && getItemCount() > 0 && (
        <button
          className="btn position-fixed"
          style={{
            bottom: '20px',
            right: isCartOpen ? '320px' : '20px',
            width: '60px',
            height: '60px',
            border: '2px solid #343a40',
            backgroundColor: '#343a40',
            color: 'white',
            borderRadius: '50%',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1000,
            transition: 'right 0.3s ease-in-out',
          }}
          onClick={toggleCart}
        >
          <i className="bi bi-basket-fill" style={{ fontSize: '1.5rem' }}></i>
          <span
            className="badge bg-danger position-absolute"
            style={{
              top: '5px',
              right: '5px',
              fontSize: '0.8rem',
            }}
          >
            {getItemCount()}
          </span>
        </button>
      )}

      {/* Slide-In Cart Drawer */}
      <div
        className={`offcanvas offcanvas-end ${isCartOpen ? 'show' : ''}`}
        style={{
          width: '300px',
          transform: isCartOpen ? 'translateX(0)' : 'translateX(100%)',
          transition: 'transform 0.3s ease-in-out',
          zIndex: 1050,
        }}
      >
        <div className="offcanvas-header bg-dark text-white">
          <h5 className="offcanvas-title">Your Cart</h5>
          <button
            type="button"
            className="btn-close btn-close-white"
            onClick={toggleCart}
          ></button>
        </div>
        <div className="offcanvas-body p-3">
          {cartItems.length > 0 ? (
            cartItems.map((item) => (
              <div
                key={item._id}
                className="d-flex align-items-center mb-3 border-bottom pb-3"
              >
                <img
                  src={item.primaryImage || '/placeholder-image.png'}
                  alt={item.name}
                  className="rounded"
                  style={{
                    width: '50px',
                    height: '50px',
                    objectFit: 'cover',
                    marginRight: '10px',
                  }}
                />
                <div className="flex-grow-1">
                  <h6 className="mb-1 text-truncate">{item.name}</h6>
                  <div className="d-flex align-items-center mt-1">
                    <button
                      className="btn btn-sm btn-outline-secondary me-2"
                      onClick={() => decrementItem(item._id)}
                    >
                      -
                    </button>
                    <span className="mx-2">{item.quantity}</span>
                    <button
                      className="btn btn-sm btn-outline-secondary ms-2"
                      onClick={() => incrementItem(item._id)}
                    >
                      +
                    </button>
                  </div>
                  <p className="mt-2 mb-0 text-muted">${item.price.toFixed(2)}</p>
                </div>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => removeFromCart(item._id)}
                >
                  <i className="bi bi-trash"></i>
                </button>
              </div>
            ))
          ) : (
            <p className="text-center text-muted">Your cart is empty.</p>
          )}
          <div>

          </div>
        </div>



        {/* Cart Footer */}
        {cartItems.length > 0 && (
          <div className="offcanvas-footer bg-light border-top p-3">
            <div className="d-flex justify-content-between mb-3">
              <span className="fw-bold">Total:</span>
              <span className="fw-bold">${calculateTotal().toFixed(2)}</span>
            </div>
            <button
              className="btn btn-primary w-100"
              onClick={() => {
                toggleCart();
                navigate('/checkout'); // Navigate to checkout page
              }}
            >
              Checkout
            </button>
          </div>
        )}
      </div>
    </>
  );
};

export default Cart;
