import React, { useContext, useState } from 'react';
import { CartContext } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const {
    cartItems,
    removeFromCart,
    incrementItem,
    decrementItem,
    calculateTotal,
    getItemCount,
  } = useContext(CartContext);
  const [isCartOpen, setCartOpen] = useState(false);
  const navigate = useNavigate();

  // Toggle the cart visibility
  const toggleCart = () => setCartOpen(!isCartOpen);

  return (
    <>
      {/* Floating Cart Button */}
      <button
        className="btn btn-primary rounded-circle position-fixed"
        style={{
          bottom: '20px',
          right: '20px',
          width: '60px',
          height: '60px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000,
        }}
        onClick={toggleCart}
      >
        <i className="bi bi-basket-fill" style={{ fontSize: '1.5rem' }}></i>
        {getItemCount() > 0 && (
          <span
            className="badge bg-danger position-absolute"
            style={{
              top: '5px',
              right: '5px',
              fontSize: '0.8rem',
            }}
          >
            {getItemCount()}
          </span>
        )}
      </button>

      {/* Slide-In Cart Drawer */}
      <div
        className={`offcanvas offcanvas-end ${isCartOpen ? 'show' : ''}`}
        style={{
          width: '300px',
          transform: isCartOpen ? 'translateX(0)' : 'translateX(100%)',
          transition: 'transform 0.3s ease-in-out',
          zIndex: 1050,
        }}
      >
        <div className="offcanvas-header">
          <h5 className="offcanvas-title">Your Cart</h5>
          <button
            type="button"
            className="btn-close"
            onClick={toggleCart}
          ></button>
        </div>
        <div className="offcanvas-body">
          {cartItems.length > 0 ? (
            cartItems.map((item) => (
              <div
                key={item._id}
                className="d-flex align-items-center mb-3"
                style={{
                  borderBottom: '1px solid #ddd',
                  paddingBottom: '10px',
                }}
              >
                {/* Thumbnail */}
                <img
                  src={item.primaryImage || '/placeholder-image.png'}
                  alt={item.name}
                  style={{
                    width: '50px',
                    height: '50px',
                    objectFit: 'cover',
                    borderRadius: '5px',
                    marginRight: '10px',
                  }}
                />
                <div className="flex-grow-1">
                  {/* Product Name */}
                  <h6 className="mb-0">{item.name}</h6>
                  {/* Quantity and Price */}
                  <div className="d-flex align-items-center mt-1">
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => decrementItem(item._id)}
                    >
                      -
                    </button>
                    <span className="mx-2">{item.quantity}</span>
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => incrementItem(item._id)}
                    >
                      +
                    </button>
                  </div>
                  <p className="mb-0 mt-1 text-muted">
                    ${item.price.toFixed(2)}
                  </p>
                </div>
                {/* Remove Item */}
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => removeFromCart(item._id)}
                >
                  <i className="bi bi-trash"></i>
                </button>
              </div>
            ))
          ) : (
            <p className="text-muted">Your cart is empty.</p>
          )}
        </div>

        {/* Cart Footer */}
        {cartItems.length > 0 && (
          <div className="offcanvas-footer border-top pt-3">
            <div className="d-flex justify-content-between mb-3">
              <span>Total:</span>
              <span>${calculateTotal().toFixed(2)}</span>
            </div>
            <button
              className="btn btn-primary w-100"
              onClick={() => {
                toggleCart();
                navigate('/checkout'); // Navigate to checkout page
              }}
            >
              Checkout
            </button>
          </div>
        )}
      </div>
    </>
  );
};

export default Cart;
