// VendorProducts.js
import React, { useEffect, useState } from 'react';

const VendorProducts = ({ vendor }) => {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch vendor products using the vendorId from the vendor object
    const fetchVendorProducts = async () => {
      try {
        const response = await fetch(`/api/products/vendor/${vendor._id}/products`); // Assuming _id is the vendor ID field
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        setError(error.message);
      }
    };

    if (vendor) {
      fetchVendorProducts();
    }
  }, [vendor]);

  if (!vendor) return <div>Loading vendor products...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <h2>Products for {vendor.dispensaryName}</h2>
      <ul>
        {products.map((product) => (
          <li key={product.productId}>
            <img src={product.primaryImage} alt={product.name} style={{ width: '100px' }} />
            <div>{product.name}</div>
            <div>Category: {product.category}</div>
            <div>Price: ${product.price}</div>
            <div>THC Content: {product.thcContent}mg</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VendorProducts;
