import React, { useEffect, useState } from 'react';
import ChangeAddressLink from './ChangeAddressLink';
import VendorCard from './VendorCard';

const NearbyVendorsPage = () => {
  const [vendors, setVendors] = useState([]);
  const [address, setAddress] = useState(localStorage.getItem('userAddress') || '');

  useEffect(() => {
    if (address) {
      // Fetch nearby vendors based on the address
      fetch(`/api/vendors/nearby?address=${encodeURIComponent(address)}`)
        .then((res) => res.json())
        .then((data) => {
          console.log(data.vendors); // Debug: Ensure products are included
          setVendors(data.vendors || []);
        })
        .catch((error) => console.error('Error fetching vendors:', error));
    }
  }, [address]);

  if (!address) {
    return <p>No address set. Please go back and enter your address.</p>;
  }

  return (
    <div className="container mt-4">
      <h1>Vendors Near You</h1>
      <ChangeAddressLink />
      {vendors.length ? (
        <div className="vendor-list">
          {vendors.map((vendor) => (
            <VendorCard key={vendor._id} vendor={vendor} />
          ))}
        </div>
      ) : (
        <p>No vendors found near your address.</p>
      )}
    </div>
  );
};

export default NearbyVendorsPage;
