import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const AddProductPage = () => {
  const { auth } = useAuth();
  const [category, setCategory] = useState('Flower');
  const [brand, setBrand] = useState('');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedImage, setSelectedImage] = useState('');
  const [customDetails, setCustomDetails] = useState({
    variation: '',
    amount: '',
    thcContent: '',
    price: '',
    salePrice: '',
    stock: '',
  });
  const [showDropdown, setShowDropdown] = useState(false);

  // Fetch products by brand
  useEffect(() => {
    if (brand) {
      fetch(`/api/products?category=${category}&brand=${brand}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand, category]);

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setSelectedImage(product.images[0] || ''); // Default to the first image
    setShowDropdown(false);
  };

  const handleAddToInventory = async () => {
    const { variation, amount, thcContent, price, salePrice, stock } = customDetails;
    if (!selectedProduct || !price || !stock || !amount) {
      alert('Please fill out all required fields.');
      return;
    }

    try {
      const response = await fetch('/api/vendor/inventory', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendorId: auth.vendorId,
          productId: selectedProduct._id,
          price,
          salePrice: salePrice || null,
          stock,
          weight: amount,
          image: selectedImage,
          variation,
          thcContent: thcContent || null,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert(data.message);
        resetForm();
      } else {
        alert(data.error || 'Failed to add product to inventory.');
      }
    } catch (error) {
      console.error('Error adding product to inventory:', error);
      alert('An error occurred while adding the product.');
    }
  };

  const resetForm = () => {
    setCategory('Flower');
    setBrand('');
    setProducts([]);
    setSelectedProduct(null);
    setSelectedImage('');
    setCustomDetails({
      variation: '',
      amount: '',
      thcContent: '',
      price: '',
      salePrice: '',
      stock: '',
    });
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Add Products to Your Inventory</h1>
      <div className="card p-4 shadow-sm">
        {/* Step 1: Select Category and Brand */}
        <div className="mb-3">
          <label htmlFor="categorySelect" className="form-label">Product Category</label>
          <select
            id="categorySelect"
            className="form-select"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option>Flower</option>
            <option>Pre-roll</option>
            <option>Vape</option>
            <option>Edible</option>
            <option>Extract</option>
            <option>Topical</option>
            <option>Gear</option>
          </select>
        </div>
        <div className="mb-3 position-relative">
          <label htmlFor="brandInput" className="form-label">Product Brand</label>
          <input
            id="brandInput"
            type="text"
            className="form-control"
            value={brand}
            onChange={(e) => {
              setBrand(e.target.value);
              setShowDropdown(true);
            }}
            placeholder="Start typing a brand name..."
          />
          {showDropdown && products.length > 0 && (
            <div className="dropdown-menu show w-100">
              {products.map((product) => (
                <button
                  key={product._id}
                  className="dropdown-item d-flex align-items-center"
                  onClick={() => handleProductSelect(product)}
                >
                  <img
                    src={product.images[0] || '/placeholder-image.png'}
                    alt={product.name}
                    className="me-2"
                    style={{ width: '50px', height: '50px', objectFit: 'cover' }}
                  />
                  <div>
                    <strong>{product.brand}</strong> - {product.name}
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Step 2: Customize Product */}
        {selectedProduct && (
          <div>
            <h5>Customize {selectedProduct.name}</h5>
            <div className="mb-3">
              <label className="form-label">Select Product Image</label>
              <div className="d-flex overflow-auto">
                {selectedProduct.images.map((img, index) => (
                  <img
                    key={index}
                    src={img}
                    alt={`Option ${index + 1}`}
                    className={`thumbnail me-2 ${selectedImage === img ? 'border border-primary' : ''}`}
                    style={{
                      width: '75px',
                      height: '75px',
                      cursor: 'pointer',
                      objectFit: 'cover',
                    }}
                    onClick={() => setSelectedImage(img)}
                  />
                ))}
              </div>
            </div>
            <div className="mb-3">
              <label className="form-label">Strain</label>
              <select
                className="form-select"
                value={customDetails.variation}
                onChange={(e) => setCustomDetails({ ...customDetails, variation: e.target.value })}
              >
                <option value="">Select Strain</option>
                {selectedProduct.variations.map((variation, index) => (
                  <option key={index} value={variation.strain}>
                    {variation.strain} ({variation.classification})
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-3">
              <label className="form-label">Amount</label>
              <select
                className="form-select"
                value={customDetails.amount}
                onChange={(e) => setCustomDetails({ ...customDetails, amount: e.target.value })}
              >
                <option value="">Select Amount</option>
                {selectedProduct.variations.map((variation, index) => (
                  <option key={index} value={variation.amount}>
                    {variation.amount}
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-3">
              <label className="form-label">THC % (Optional)</label>
              <input
                type="number"
                className="form-control"
                placeholder="e.g., 20.5"
                value={customDetails.thcContent}
                onChange={(e) => setCustomDetails({ ...customDetails, thcContent: e.target.value })}
              />
            </div>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label className="form-label">Price</label>
                <input
                  type="number"
                  className="form-control"
                  value={customDetails.price}
                  onChange={(e) => setCustomDetails({ ...customDetails, price: e.target.value })}
                />
              </div>
              <div className="col-md-6 mb-3">
                <label className="form-label">Sale Price (Optional)</label>
                <input
                  type="number"
                  className="form-control"
                  value={customDetails.salePrice}
                  onChange={(e) => setCustomDetails({ ...customDetails, salePrice: e.target.value })}
                />
              </div>
            </div>
            <div className="mb-3">
              <label className="form-label">Stock</label>
              <input
                type="number"
                className="form-control"
                value={customDetails.stock}
                onChange={(e) => setCustomDetails({ ...customDetails, stock: e.target.value })}
              />
            </div>
            <button className="btn btn-success w-100" onClick={handleAddToInventory}>
              Add to Inventory
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default AddProductPage;
