import React, { useContext, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { VendorProvider, VendorContext } from '../context/VendorContext';
import VendorDetails from '../components/VendorDetails';
import MenuDisplay from '../components/MenuDisplay';
import CategoryFilter from '../components/CategoryFilter';
import './VendorMenuPage.css';

const VendorMenuPage = () => {
  const { vendorId } = useParams();

  return (
    <VendorProvider vendorId={vendorId}>
      <VendorMenuContent />
    </VendorProvider>
  );
};

const VendorMenuContent = () => {
  const { vendor, products, loading, error } = useContext(VendorContext);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState('');
  const [activeFilters, setActiveFilters] = useState({
    sativa: false,
    hybrid: false,
    indica: false,
  });

  // State for animation classes
  const [animateVendorDetails, setAnimateVendorDetails] = useState(false);
  const [animateCategoryFilter, setAnimateCategoryFilter] = useState(false);
  const [animateMenuDisplay, setAnimateMenuDisplay] = useState(false);

  useEffect(() => {
    const applyFilters = () => {
      let updatedProducts = products;

      // Filter by category
      if (selectedCategory !== 'All') {
        updatedProducts = updatedProducts.filter(
          (product) => product.category === selectedCategory
        );
      }

      // Filter by classifications
      const activeClassifications = Object.entries(activeFilters)
        .filter(([_, isActive]) => isActive)
        .map(([classification]) => classification.toLowerCase());

      if (activeClassifications.length > 0) {
        updatedProducts = updatedProducts.filter((product) => {
          const strain = product.strain?.toLowerCase() || '';
          return activeClassifications.some((filter) => {
            if (filter === 'sativa') return strain.includes('sat');
            if (filter === 'hybrid') return strain.includes('hyb');
            if (filter === 'indica') return strain.includes('ind');
            return false;
          });
        });
      }

      // Filter by search term
      if (searchTerm.trim()) {
        updatedProducts = updatedProducts.filter((product) =>
          [product.name, product.brand, product.strain]
            .filter(Boolean)
            .some((field) =>
              field.toLowerCase().includes(searchTerm.trim().toLowerCase())
            )
        );
      }

      setFilteredProducts(updatedProducts);
    };

    applyFilters();
  }, [selectedCategory, activeFilters, searchTerm, products]);

  useEffect(() => {
    // Trigger animations sequentially
    setTimeout(() => setAnimateVendorDetails(true), 100);
    setTimeout(() => setAnimateCategoryFilter(true), 300);
    setTimeout(() => setAnimateMenuDisplay(true), 500);
  }, []);

  const handleCategoryChange = (category) => setSelectedCategory(category);
  const handleSearch = (term) => setSearchTerm(term);
  const handleFilter = (filters) => setActiveFilters(filters);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div
      className="vendor-menu-page"
      style={{ backgroundColor: 'black', minHeight: '100vh', padding: '0px' }}
    >
      <div className={`vendor-details-container ${animateVendorDetails ? 'slide-in' : ''}`}>
        <VendorDetails vendor={vendor} />
      </div>
      <div className={`category-filter-container ${animateCategoryFilter ? 'slide-in' : ''}`}>
        <CategoryFilter
          selectedCategory={selectedCategory}
          onCategoryChange={handleCategoryChange}
          onSearch={handleSearch}
          onFilter={handleFilter}
        />
      </div>
      <div className={`menu-display-container ${animateMenuDisplay ? 'slide-in' : ''}`}>
        <MenuDisplay
          products={filteredProducts}
          vendorName={vendor?.dispensaryName}
          logoUrl={vendor?.logoUrl}
          vendorId={vendor?._id}
        />
      </div>
    </div>
  );
};

export default VendorMenuPage;
