import React, { useState, useEffect, useRef } from "react";
import { Form, Button, Alert, Row, Col, InputGroup, Card } from "react-bootstrap";
import axios from "axios";
import { useJsApiLoader } from "@react-google-maps/api";

const VendorRegistration = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    dispensaryName: "",
    dispensaryType: "",
    storefrontAddress: "",
    minOrder: "",
    acceptedPayments: [{ method: "Cash", fee: 0 }],
  });
  const [formErrors, setFormErrors] = useState({});
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);
  const inputRef = useRef(null);

  const { isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ["places"],
  });

  useEffect(() => {
    if (!isLoaded || !inputRef.current) return;

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ["geocode"],
    });

    autocomplete.addListener("place_changed", () => {
      const place = autocomplete.getPlace();
      if (place?.formatted_address) {
        setFormData((prev) => ({ ...prev, storefrontAddress: place.formatted_address }));
      }
    });
  }, [isLoaded, inputRef.current]);


  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handlePhoneChange = (e) => {
    const rawValue = e.target.value.replace(/\D/g, "");
    const formatted = rawValue.replace(
      /^(\d{0,3})(\d{0,3})(\d{0,4})$/,
      (match, areaCode, prefix, lineNumber) => {
        if (lineNumber) return `(${areaCode}) ${prefix}-${lineNumber}`;
        if (prefix) return `(${areaCode}) ${prefix}`;
        if (areaCode) return `(${areaCode}`;
        return "";
      }
    );
    setFormData((prev) => ({ ...prev, contactNumber: formatted }));
  };

  const handlePaymentFeeChange = (index, value) => {
    setFormData((prev) => {
      const updatedPayments = [...prev.acceptedPayments];
      updatedPayments[index].fee = parseFloat(value) || 0;
      return { ...prev, acceptedPayments: updatedPayments };
    });
  };

  const validateStepOne = () => {
    const errors = {};
    if (!formData.firstName.trim()) errors.firstName = "First name is required";
    if (!formData.lastName.trim()) errors.lastName = "Last name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) errors.email = "Invalid email address";
    if (formData.contactNumber.replace(/\D/g, "").length !== 10) {
      errors.contactNumber = "Phone number must be 10 digits";
    }
    if (formData.password.length < 6) errors.password = "Password must be at least 6 characters";
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (validateStepOne()) {
      setCurrentStep(2);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formattedFormData = {
        ...formData,
        minOrder: parseFloat(formData.minOrder) || 0,
      };
      const response = await axios.post("/api/auth/register", formattedFormData);
      if (response.data.success) setSuccess(true);
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor.");
    }
  };

  if (loadError) return <Alert variant="danger">Error loading Google Maps API</Alert>;
  if (!isLoaded) return <p>Loading...</p>;

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <Card className="w-50 p-4">
        <Card.Body>
          <h2 className="mb-4 text-center">Vendor Registration</h2>
          {success && <Alert variant="success">Registration successful!</Alert>}
          {error && <Alert variant="danger">{error}</Alert>}

          {currentStep === 1 && (
            <Form onSubmit={handleNext}>
              {/* Step 1: Personal Info */}
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                      type="text"
                      name="firstName"
                      value={formData.firstName}
                      onChange={handleChange}
                      isInvalid={!!formErrors.firstName}
                    />
                    <Form.Control.Feedback type="invalid">
                      {formErrors.firstName}
                    </Form.Control.Feedback>
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                      type="text"
                      name="lastName"
                      value={formData.lastName}
                      onChange={handleChange}
                      isInvalid={!!formErrors.lastName}
                    />
                    <Form.Control.Feedback type="invalid">
                      {formErrors.lastName}
                    </Form.Control.Feedback>
                  </Form.Group>
                </Col>
              </Row>
              <Form.Group className="mb-3">
                <Form.Label>Phone Number</Form.Label>
                <Form.Control
                  type="text"
                  name="contactNumber"
                  value={formData.contactNumber}
                  onChange={handlePhoneChange}
                  isInvalid={!!formErrors.contactNumber}
                />
                <Form.Control.Feedback type="invalid">
                  {formErrors.contactNumber}
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  isInvalid={!!formErrors.email}
                />
                <Form.Control.Feedback type="invalid">
                  {formErrors.email}
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  isInvalid={!!formErrors.password}
                />
                <Form.Control.Feedback type="invalid">
                  {formErrors.password}
                </Form.Control.Feedback>
              </Form.Group>
              <Button type="submit" variant="primary" className="w-100">
                Next
              </Button>
            </Form>
          )}

          {currentStep === 2 && (
            <Form onSubmit={handleSubmit}>
              {/* Step 2: Dispensary Details */}
              <Form.Group className="mb-3">
                <Form.Label>Dispensary Name</Form.Label>
                <Form.Control
                  type="text"
                  name="dispensaryName"
                  value={formData.dispensaryName}
                  onChange={handleChange}
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Dispensary Type</Form.Label>
                <div>
                  {["DELIVERY", "PICKUP", "PICKUP & DELIVERY"].map((type) => (
                    <Form.Check
                      key={type}
                      type="radio"
                      name="dispensaryType"
                      label={type}
                      value={type}
                      onChange={handleChange}
                    />
                  ))}
                </div>
              </Form.Group>
              {["PICKUP", "PICKUP & DELIVERY"].includes(formData.dispensaryType) && (
                <Form.Group className="mb-3">
                  <Form.Label>Storefront Address</Form.Label>
                  <Form.Control
                    type="text"
                    ref={inputRef}
                    name="storefrontAddress"
                    value={formData.storefrontAddress}
                    onChange={handleChange}
                  />
                </Form.Group>
              )}
              <Form.Group className="mb-3">
                <Form.Label>Minimum Order</Form.Label>
                <InputGroup>
                  <InputGroup.Text>$</InputGroup.Text>
                  <Form.Control
                    type="number"
                    name="minOrder"
                    value={formData.minOrder}
                    onChange={handleChange}
                  />
                </InputGroup>
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Accepted Payments</Form.Label>
                <div>
                  {["Cash", "Debit", "Credit"].map((method, index) => (
                    <div key={method} className="d-flex align-items-center mb-2">
                      <Form.Check
                        type="checkbox"
                        id={method}
                        label={method}
                        checked={formData.acceptedPayments.some(
                          (payment) => payment && payment.method === method
                        )}
                        onChange={(e) =>
                          setFormData((prev) => {
                            const updatedPayments = prev.acceptedPayments.filter(
                              (payment) => payment && payment.method !== method
                            );
                            if (e.target.checked) {
                              updatedPayments.push({ method, fee: 0 });
                            }
                            return { ...prev, acceptedPayments: updatedPayments };
                          })
                        }
                      />
                      {(method === "Debit" || method === "Credit") &&
                        formData.acceptedPayments.some(
                          (payment) => payment && payment.method === method
                        ) && (
                          <InputGroup className="ms-3" style={{ maxWidth: "150px" }}>
                            <InputGroup.Text>Fee:</InputGroup.Text>
                            <InputGroup.Text>$</InputGroup.Text>
                            <Form.Control
                              type="number"
                              maxLength="2"
                              placeholder="0"
                              value={
                                formData.acceptedPayments.find(
                                  (payment) => payment && payment.method === method
                                )?.fee || ""
                              }
                              onChange={(e) =>
                                handlePaymentFeeChange(index, e.target.value.slice(0, 2))
                              }
                            />
                          </InputGroup>
                        )}
                    </div>
                  ))}
                </div>
              </Form.Group>
              <Button type="submit" variant="primary" className="w-100">
                Register
              </Button>
            </Form>
          )}
        </Card.Body>
      </Card>
    </div>
  );
};

export default VendorRegistration;
