import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const { calculateTotal, cartItems, clearCart } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    day: '',
    month: '',
    year: '',
  });

  const [deliveryAddress, setDeliveryAddress] = useState({
    street: '',
    city: '',
    state: '',
    zip: '',
  });

  const [isEditing, setIsEditing] = useState(false); // Toggle editing mode
  const navigate = useNavigate();

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  // Load address from localStorage on component mount
  useEffect(() => {
    const savedAddress = JSON.parse(localStorage.getItem('deliveryAddress'));
    if (savedAddress) {
      setDeliveryAddress(savedAddress);
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setContactInfo({ ...contactInfo, [name]: value });
  };

  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setDeliveryAddress({ ...deliveryAddress, [name]: value });
  };

  const handleSaveAddress = () => {
    localStorage.setItem('deliveryAddress', JSON.stringify(deliveryAddress));
    setIsEditing(false);
  };

  const handlePlaceOrder = async () => {
    // Implement order placement logic here
  };

  return (
    <div className="place-order-section p-4 bg-light">
      {/* Delivery Address Section */}
      <div className="mb-4">
        <h4>Delivery Address</h4>
        {isEditing ? (
          <Form>
            <Form.Group controlId="street" className="mb-3">
              <Form.Label>Street Address</Form.Label>
              <Form.Control
                type="text"
                name="street"
                value={deliveryAddress.street}
                onChange={handleAddressChange}
                required
              />
            </Form.Group>
            <Row className="mb-3">
              <Col>
                <Form.Label>City</Form.Label>
                <Form.Control
                  type="text"
                  name="city"
                  value={deliveryAddress.city}
                  onChange={handleAddressChange}
                  required
                />
              </Col>
              <Col>
                <Form.Label>State</Form.Label>
                <Form.Control
                  as="select"
                  name="state"
                  value={deliveryAddress.state}
                  onChange={handleAddressChange}
                  required
                >
                  <option value="">Select</option>
                  {['CA', 'CO', 'NY', 'FL', 'NV', 'OR', 'NJ'].map((state) => (
                    <option key={state} value={state}>{state}</option>
                  ))}
                </Form.Control>
              </Col>
              <Col>
                <Form.Label>ZIP Code</Form.Label>
                <Form.Control
                  type="text"
                  name="zip"
                  value={deliveryAddress.zip}
                  onChange={handleAddressChange}
                  required
                />
              </Col>
            </Row>
            <Button variant="success" onClick={handleSaveAddress}>Save Address</Button>
          </Form>
        ) : (
          <Card>
            <Card.Body>
              <Card.Text>
                <strong>Street:</strong> {deliveryAddress.street || 'N/A'}
              </Card.Text>
              <Card.Text>
                <strong>City:</strong> {deliveryAddress.city || 'N/A'}
              </Card.Text>
              <Card.Text>
                <strong>State:</strong> {deliveryAddress.state || 'N/A'}
              </Card.Text>
              <Card.Text>
                <strong>ZIP Code:</strong> {deliveryAddress.zip || 'N/A'}
              </Card.Text>
              <Button variant="primary" onClick={() => setIsEditing(true)}>Edit</Button>
            </Card.Body>
          </Card>
        )}
      </div>

      {/* Contact Information Section */}
      <div className="mb-4">
        {/* Existing Contact Information Form */}
        {/* ... */}
      </div>

      {/* Order Summary Section */}
      <div className="mb-4">
        {/* Existing Order Summary */}
        {/* ... */}
      </div>

      {/* Place Order Button */}
      <Button variant="primary" className="w-100 mb-3" onClick={handlePlaceOrder}>
        Place Order
      </Button>
    </div>
  );
};

export default TestPlaceOrder;
