import React, { useState, useEffect } from 'react';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    // Fetch products from backend
    const fetchProducts = async () => {
        try {
            const response = await fetch('http://localhost:4000/api/products');
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            // Access product_list within the data structure
            setProducts(data.data.product_list || []);
            console.log("Fetched Products:", data);
        } catch (err) {
            setError(`Error fetching products: ${err.message}`);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <div className="container my-4">
            <h2>Product List</h2>
            {error && <p className="text-danger">{error}</p>}
            <div className="row">
                {products.length > 0 ? (
                    products.map((product) => (
                        <div className="col-md-4 mb-3" key={product.product_id}>
                            <div className="card">
                                <img
                                    src={product.e_commerce?.primary_image || 'https://via.placeholder.com/150'}
                                    alt={product.product_configurable_fields.name}
                                    className="card-img-top"
                                />
                                <div className="card-body">
                                    <h5 className="card-title">{product.product_configurable_fields.name}</h5>
                                    <p className="card-text">Price: ${product.pricing.price_sell}</p>
                                    <p className="card-text">Category: {product.category_type}</p>
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No products available</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
