import React, { useState } from 'react';
// import { Row, Col, Image, Button, Modal } from 'react-bootstrap';
import VendorStatus from './VendorStatus';
import MenuPickupDeliveryToggle from './MenuPickupDeliveryToggle';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter
} from '@/components/ui/dialog';
import { Tag } from 'lucide-react';
//import './VendorDetails.css';

const VendorDetails = ({ vendor, orderType, setOrderType }) => {
  const [showPromoModal, setShowPromoModal] = useState(false);

  if (!vendor || !vendor.businessHours) {
    return <p>Loading vendor details...</p>;
  }

  const getVendorStatus = () => {
    const currentDay = new Date().toLocaleString('en-US', { weekday: 'long' }).toLowerCase();
    const currentTime = new Date();
    const openTime = new Date(
      `${currentDay} ${vendor.businessHours[currentDay]?.open || '00:00'}`
    );
    const closeTime = new Date(
      `${currentDay} ${vendor.businessHours[currentDay]?.close || '23:59'}`
    );

    if (currentTime >= openTime && currentTime <= closeTime) {
      return { status: 'open', until: vendor.businessHours[currentDay]?.close };
    }

    const days = Object.keys(vendor.businessHours);
    const nextDayIndex = (days.indexOf(currentDay) + 1) % days.length;
    const nextDay = days[nextDayIndex];
    return { status: 'closed', until: vendor.businessHours[nextDay]?.open };
  };

  const vendorStatus = getVendorStatus();

  return (

    <Card className="bg-gray-50 rounded-t-2xl border-none shadow-lg">
      {/* Main Content Container */}
      <div className="p-4 space-y-3">
        {/* Header Row with Logo and Status */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <img
              src={vendor.logoUrl || '/placeholder-logo.png'}
              alt={`${vendor.dispensaryName} logo`}
              className="w-12 h-12 rounded-full object-cover"
            />
            <div>
              <h2 className="font-semibold text-lg">{vendor.dispensaryName}</h2>
              <div className="flex items-center space-x-2">
                <span className="text-sm text-gray-600">${vendor.minOrder} minimum</span>
                <span className="text-gray-400">â€¢</span>
                <VendorStatus vendor={vendor} vendorStatus={vendorStatus} />
              </div>
            </div>
          </div>

          <MenuPickupDeliveryToggle
            vendor={vendor}
            orderType={orderType}
            setOrderType={setOrderType}
            className="scale-90"
          />
        </div>

        {/* Payment Methods */}
        <div className="flex flex-wrap items-center gap-2">
          {vendor.acceptedPayments.map((payment, index) => (
            <Badge 
              key={index}
              variant="outline"
              className="bg-white text-xs font-normal py-0.5"
            >
              {payment.method}
            </Badge>
          ))}
        </div>

        {/* Promo Section */}
        {vendor.dailyPromo?.title && (
          <div className="pt-1">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowPromoModal(true)}
              className="flex items-center space-x-2 text-xs h-8 rounded-full hover:bg-gray-100"
            >
              <Tag className="w-4 h-4" />
              <span className="truncate max-w-[150px]">
                {vendor.dailyPromo.title}
              </span>
            </Button>
          </div>
        )}
      </div>

      {/* Promo Modal */}
      <Dialog open={showPromoModal} onOpenChange={setShowPromoModal}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>{vendor.dailyPromo?.title}</DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <p className="text-gray-600">
              {vendor.dailyPromo?.description || 'No description provided.'}
            </p>

            {vendor.dailyPromo?.promoUrl && (
              <div className="text-center">
                <img
                  src={vendor.dailyPromo.promoUrl}
                  alt="Promo"
                  className="mx-auto max-w-[90%] rounded-lg"
                />
                <p className="text-sm text-gray-500 mt-2">
                  Present coupon in-person before payment to apply.
                </p>
              </div>
            )}

            {vendor.dailyPromo?.applicableToSaleItems !== undefined && (
              <p className={`font-medium ${
                vendor.dailyPromo.applicableToSaleItems 
                  ? 'text-green-600' 
                  : 'text-red-600'
              }`}>
                {vendor.dailyPromo.applicableToSaleItems
                  ? 'Applicable to Sale Items!'
                  : 'Not applicable to sale items.'}
              </p>
            )}
          </div>

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setShowPromoModal(false)}
            >
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </Card>
  );
};

export default VendorDetails;
