import React, { useState, useEffect } from 'react';
import { Form, Button, InputGroup } from 'react-bootstrap';

const MinOrderComponent = ({ initialMinOrder = null, onUpdate }) => {
  const [minOrder, setMinOrder] = useState(initialMinOrder);

  useEffect(() => {
    setMinOrder(initialMinOrder); // Update state when initialMinOrder changes
  }, [initialMinOrder]);

  const handleSelectChange = (e) => {
    const value = e.target.value === 'null' ? null : Number(e.target.value); // Convert to null if "Null" is selected
    setMinOrder(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (minOrder === null) {
      alert('Please select a valid minimum order amount.');
      return;
    }
    onUpdate(minOrder); // Ensure the value is sent as a number
  };

  return (
    <Form onSubmit={handleSubmit} className="p-4 border rounded shadow-sm">
      <h5 className="mb-3">Set Minimum Order</h5>
      <Form.Group className="mb-3">
        <Form.Label>Minimum Order Amount</Form.Label>
        <InputGroup>
          <InputGroup.Text>$</InputGroup.Text>
          <Form.Select value={minOrder === null ? 'null' : minOrder} onChange={handleSelectChange}>
            <option value="">No Minimum</option> {/* Placeholder for no initial minOrder */}
            <option value="10">10</option>
            <option value="20">20</option>
            <option value="30">30</option>
            <option value="40">40</option>
            <option value="50">50</option>
            <option value="60">60</option>
          </Form.Select>
        </InputGroup>
      </Form.Group>
      <div className="d-flex justify-content-center">
        <Button variant="primary" type="submit" className="mt-3 px-4 py-2">
          Update Minimum Order
        </Button>
      </div>
      <Button variant="primary" type="submit">
        Update Minimum Order
      </Button>
    </Form>
  );
};

export default MinOrderComponent;
