// ProductRow.js

import React, { useContext } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './ProductRow.css';
import { CartContext } from '../context/CartContext';

const ProductRow = ({
  id,
  image,
  brand,
  productName,
  productAmount,
  price,
  salePrice,
}) => {
  const { addToCart } = useContext(CartContext);

  const handleAddToCart = () => {
    const product = {
      id,
      brand,
      productName,
      productAmount,
      price: salePrice || price,
      salePrice,
      image,
    };

    addToCart(product);
  };

  return (
    <div className="row product-row align-items-center py-3">
      <div className="col-2">
        <img src={image} alt={productName} className="img-fluid product-image" />
      </div>

      <div className="col-6">
        <p className="mb-1 text-muted fw-light brand-name">{brand}</p>
        <p className="mb-1">
          <span className="fw-bold product-name">{productName}</span>
          {' | '}
          <span className="fw-normal product-amount">{productAmount}</span>
        </p>
      </div>

      <div className="col-4 text-end">
        <button
          className="btn btn-black-pill d-flex justify-content-center align-items-center position-relative float-start w-100"
          onClick={handleAddToCart}
          style={{ maxWidth: '200px' }}
        >
          <span className="text-white fw-bold position-absolute plus-sign" style={{ left: '10px' }}>+</span>

          {salePrice ? (
            <>
              <span className="me-2 fw-bold">${salePrice}</span>
              <del>${price}</del>
            </>
          ) : (
            <span className="fw-bold">${price}</span>
          )}
        </button>
      </div>
    </div>
  );
};

export default ProductRow;
