import React, { createContext, useState, useEffect } from 'react';

// Create a Context for the cart
export const CartContext = createContext();

export const CartProvider = ({ children }) => {

  // State to hold cart items
  const [cartItems, setCartItems] = useState([]);

  // Add product to the cart
  const addToCart = (product) => {
    setCartItems((prevItems) => {
      const existingItem = prevItems.find(item => item.productId === product.productid);
      if (existingItem) {
        return prevItems.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevItems, { ...product, quantity: 1 }];
      }
    });
  };

  // Remove product from the cart
  const removeFromCart = (productId) => {
    setCartItems((prevItems) =>
      prevItems.filter(item => item.id !== productId) // Filter out the product with the matching ID
    );
  };

  // Increment quantity of a specific item in the cart
  const incrementItem = (productId) => {
    setCartItems((prevItems) =>
      prevItems.map(item =>
        item.id === productId ? { ...item, quantity: item.quantity + 1 } : item
      )
    );
  };

  // Decrement quantity of a specific item in the cart
  const decrementItem = (productId) => {
    setCartItems((prevItems) =>
      prevItems.map(item =>
        item.id === productId && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      )
    );
  };

  // Update quantity of a specific item in the cart manually
  const updateItemQuantity = (productId, newQuantity) => {
    setCartItems((prevItems) =>
      prevItems.map(item =>
        item.id === productId ? { ...item, quantity: newQuantity } : item
      )
    );
  };

  // Clear all items from the cart
  const clearCart = () => {
    setCartItems([]);
  };

  // Calculate total price of items in the cart
  const calculateTotal = () => {
    return cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);
  };

  // Get the total number of items in the cart (quantity, not unique items)
  const getItemCount = () => {
    return cartItems.reduce((acc, item) => acc + item.quantity, 0);
  };

  // Save cart items to localStorage
  const saveCartToLocalStorage = () => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
  };

  // Load cart items from localStorage on initial load
  const loadCartFromLocalStorage = () => {
    const savedCartItems = JSON.parse(localStorage.getItem('cartItems'));
    if (savedCartItems) {
      setCartItems(savedCartItems);
    }
  };

  // Automatically load the cart from localStorage when the component mounts
  useEffect(() => {
    loadCartFromLocalStorage();
  }, []);

  // Save cart to localStorage whenever the cartItems state changes
  useEffect(() => {
    saveCartToLocalStorage();
  }, [cartItems]);

  // Provide cart data and functions to the rest of the app
  const value = {
    cartItems,
    addToCart,
    removeFromCart,
    incrementItem,    // Exposed increment function
    decrementItem,    // Exposed decrement function
    updateItemQuantity,
    clearCart,
    calculateTotal,
    getItemCount,
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
