import React, { useState, useContext } from "react";
import { Form, Button, Card, Alert, Modal } from "react-bootstrap";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import api from "../api/api";

const Login = () => {
  const { setAuth } = useContext(AuthContext);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [showRegister, setShowRegister] = useState(false); // Controls registration modal
  const [step, setStep] = useState(1); // Registration step tracker
  const [formData, setFormData] = useState({
    licenseState: "",
    licenseNumber: "",
    dispensaryType: "",
    businessAddress: {
      street: "",
      city: "",
      state: "",
      zip: "",
    },
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    termsAccepted: false,
  });

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await api.post("/auth/login", { email, password });

      if (response.data.success && response.data.token && response.data.vendorId) {
        localStorage.setItem("token", response.data.token);
        setAuth({
          token: response.data.token,
          vendorId: response.data.vendorId,
        });
        navigate("/vendor-dashboard");
      } else {
        setError("Login failed. Please check your credentials and try again.");
      }
    } catch (err) {
      setError(err.response?.data?.message || "An error occurred during login.");
    }
  };

  const handleRegisterSubmit = async () => {
    try {
      const response = await api.post("/vendors/register", formData);

      if (response.data.success) {
        alert("Registration successful!");
        setShowRegister(false);
      } else {
        alert("Registration failed. Please try again.");
      }
    } catch (err) {
      alert("An error occurred during registration.");
    }
  };

  const handleNextStep = () => setStep((prev) => prev + 1);
  const handlePrevStep = () => setStep((prev) => prev - 1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleNestedChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  return (
    <div className="container mt-5" style={{ maxWidth: "400px" }}>
      <Card className="shadow-sm p-4">
        <Card.Body>
          <h3 className="text-center mb-4">Vendor Sign In</h3>
          {error && <Alert variant="danger">{error}</Alert>}

          <Form onSubmit={handleLogin}>
            <Form.Group controlId="email" className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="password" className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </Form.Group>

            <div className="d-flex justify-content-between align-items-center mt-4">
              <Button variant="primary" type="submit" className="w-50">
                Sign In
              </Button>
              <Button
                variant="link"
                className="text-primary ms-3"
                onClick={() => setShowRegister(true)}
              >
                Register
              </Button>
            </div>
          </Form>
        </Card.Body>
      </Card>

      {/* Registration Modal */}
      <Modal show={showRegister} onHide={() => setShowRegister(false)} centered>
        <Modal.Header closeButton>
          <div className="w-100 text-center"><Modal.Title>{`${step}/3`}</Modal.Title></div>
        </Modal.Header>
        <Modal.Body>
          {step === 1 && (
            <>
              <h5>Dispensary Info</h5>
              <Form.Group className="mb-3">
  <Form.Label>License</Form.Label>
  <div className="d-flex align-items-center">
    <Form.Select
      name="licenseState"
      value={formData.licenseState}
      onChange={handleChange}
      required
      style={{ width: "160px" }} // Adjust width to fit 'Select State'
    >
      <option value="">Select State</option>
      <option value="CA">AZ</option>

      <option value="CA">CA</option>
      <option value="NV">CO</option>
      <option value="NV">MI</option>
      <option value="NV">NJ</option>
      <option value="NV">NV</option>
      <option value="NV">NY</option>
      <option value="NV">OR</option>
      {/* Add more states */}
    </Form.Select>
    <Form.Control
      type="text"
      placeholder="License Number"
      name="licenseNumber"
      value={formData.licenseNumber}
      onChange={handleChange}
      required
      className="ms-3 flex-grow-1"
    />
  </div>
</Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Service Provided</Form.Label>
                <Form.Select
                    name="dispensaryType"
                    value={formData.dispensaryType}
                    onChange={(e) => setFormData({ ...formData, dispensaryType: e.target.value })}
                    required
                >
                    <option value="Delivery">Delivery</option>
                    <option value="Pickup">Pickup</option>
                    <option value="Both">Pickup & Delivery</option>
                </Form.Select>
                </Form.Group>

              <Form.Group>
                <Form.Label>Business Address</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Street"
                  value={formData.businessAddress.street}
                  onChange={(e) =>
                    handleNestedChange("businessAddress", "street", e.target.value)
                  }
                />
                <Form.Control
                  type="text"
                  placeholder="City"
                  className="mt-2"
                  value={formData.businessAddress.city}
                  onChange={(e) =>
                    handleNestedChange("businessAddress", "city", e.target.value)
                  }
                />
                <Form.Control
                  type="text"
                  placeholder="State"
                  className="mt-2"
                  value={formData.businessAddress.state}
                  onChange={(e) =>
                    handleNestedChange("businessAddress", "state", e.target.value)
                  }
                />
                <Form.Control
                  type="text"
                  placeholder="Zip Code"
                  className="mt-2"
                  value={formData.businessAddress.zip}
                  onChange={(e) =>
                    handleNestedChange("businessAddress", "zip", e.target.value)
                  }
                />
              </Form.Group>
            </>
          )}
          {step === 2 && (
            <>
              <h5>Business Owner Info</h5>
              {/* Owner Info */}
            </>
          )}
          {step === 3 && (
            <>
              <h5>Terms & Conditions</h5>
              {/* Scrollable terms field */}
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          {step > 1 && <Button variant="secondary" onClick={handlePrevStep}>Back</Button>}
          {step < 3 && <Button variant="primary" onClick={handleNextStep}>Next</Button>}
          {step === 3 && (
            <Button variant="success" onClick={handleRegisterSubmit}>
              Register
            </Button>
          )}
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default Login;
