// File: src/components/AuthTestButton.js
import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const AuthTestButton = () => {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);

  const handleCreateTicket = async () => {
    setLoading(true);

    const payload = {
      phone: '5555551014', // Hardcoded phone number
      orderDetails: {
        type: 'PICKUP',
        order_source: 'ECOMMERCE',
        order_status: 'AWAITING_PROCESSING',
        items: [
          {
            product_id: 'product123',
            size_id: '83e1a132-ed63-40da-951a-cdb4183acc86',
            quantity: 1,
            apply_automatic_discounts: true,
          },
        ],
        webhook: {
          listener_Url: 'https://example-webhook-url.com/',
        },
      },
    };

    try {
      const response = await fetch('http://localhost:5001/api/customer/create-ticket', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const statusCode = response.status;
      const data = await response.json();

      // Display response and status code in a popup window
      alert(`Status Code: ${statusCode}\nResponse: ${JSON.stringify(data, null, 2)}`);
      setResult(data);
    } catch (error) {
      // Handle errors
      alert(`Error occurred: ${error.message}`);
      console.error('Error details:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex flex-column align-items-center mt-5">
      <Button
        variant="primary"
        onClick={handleCreateTicket}
        disabled={loading}
        className="test-auth-button"
      >
        {loading ? 'Processing...' : 'Create Ticket'}
      </Button>
      {result && (
        <div className="mt-3 text-center">
          <strong>Result:</strong>
          <pre style={{ textAlign: 'left' }}>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

export default AuthTestButton;
