import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddressPage = () => {
  const [address, setAddress] = useState('');
  const navigate = useNavigate();
  const inputRef = useRef(null);

  useEffect(() => {
    if (!window.google) {
      console.error('Google Maps API is not loaded.');
      return;
    }

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['geocode'], // Restrict to address suggestions
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place && place.formatted_address) {
        setAddress(place.formatted_address);
      }
    });
  }, []);

  const handleSubmit = () => {
    if (address.trim()) {
      // Save the address in state, context, or localStorage
      localStorage.setItem('userAddress', address);

      // Navigate to the nearby vendors page
      navigate('/nearby');
    } else {
      alert('Please enter a valid address.');
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: '#343a40' }}>
      <div className="card shadow" style={{ width: '30rem', padding: '1.5rem' }}>
        <div className="card-body text-center">
          <h1 className="card-title mb-4">Welcome to WeedHurry</h1>
          <p className="card-text mb-4">Please enter your address to find nearby vendors:</p>
          <input
            type="text"
            ref={inputRef}
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            placeholder="Enter your address"
            className="form-control mb-3"
          />
          <button className="btn btn-primary btn-block" onClick={handleSubmit}>
            Find Vendors
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddressPage;
