import React from 'react';

const DeliveryPickupToggle = ({ selectedOption, setSelectedOption }) => {
  return (
    <div 
      className="relative inline-flex rounded shadow-sm" 
      role="group" 
      aria-label="Delivery or Pickup toggle"
    >
      {/* Delivery Button */}
      <input
        type="radio"
        className="peer/delivery sr-only"
        name="btnradio"
        id="btnradio1"
        autoComplete="off"
        checked={selectedOption === 'Delivery'}
        onChange={() => setSelectedOption('Delivery')}
      />
      <label
        className={`
          relative btn border-2 border-gray-900 rounded-l px-3 py-1 text-sm
          transition-all duration-200 ease-in-out
          ${selectedOption === 'Delivery' 
            ? 'bg-gray-900 text-white font-semibold animate-flash' 
            : 'bg-white text-gray-600 font-normal hover:bg-gray-50'
          }
        `}
        htmlFor="btnradio1"
      >
        Delivery
        {selectedOption === 'Delivery' && (
          <div className="absolute inset-0 bg-white/20 animate-ripple rounded-l" />
        )}
      </label>

      {/* Pickup Button */}
      <input
        type="radio"
        className="peer/pickup sr-only"
        name="btnradio"
        id="btnradio3"
        autoComplete="off"
        checked={selectedOption === 'Pickup'}
        onChange={() => setSelectedOption('Pickup')}
      />
      <label
        className={`
          relative btn border-2 border-gray-900 rounded-r px-3 py-1 text-sm -ml-[2px]
          transition-all duration-200 ease-in-out
          ${selectedOption === 'Pickup' 
            ? 'bg-gray-900 text-white font-semibold animate-flash' 
            : 'bg-white text-gray-600 font-normal hover:bg-gray-50'
          }
        `}
        htmlFor="btnradio3"
      >
        Pickup
        {selectedOption === 'Pickup' && (
          <div className="absolute inset-0 bg-white/20 animate-ripple rounded-r" />
        )}
      </label>

      <style jsx>{`
        @keyframes flash {
          0% { opacity: 1; }
          50% { opacity: 0.8; }
          100% { opacity: 1; }
        }

        @keyframes ripple {
          0% { opacity: 1; }
          50% { opacity: 0; }
          100% { opacity: 1; }
        }

        .animate-flash {
          animation: flash 0.3s ease-in-out;
        }

        .animate-ripple {
          animation: ripple 0.6s ease-in-out;
        }
      `}</style>
    </div>
  );
};

export default DeliveryPickupToggle;
