// ProductList.js

import React, { useState, useEffect } from 'react';
import ProductRow from './ProductRow';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    // Fetch products from backend
    const fetchProducts = async () => {
        try {
            const response = await fetch('http://localhost:4000/api/vendors/:vendorId/products'); // Adjust URL for specific vendor
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            const data = await response.json();
            setProducts(data); // Assuming data is already adapted for the frontend
        } catch (err) {
            setError(`Error fetching products: ${err.message}`);
        }
    };

    useEffect(() => {
        fetchProducts();
    }, []);

    return (
        <div className="container my-4">
            <h2>Product List</h2>
            {error && <p className="text-danger">{error}</p>}
            <div className="row">
                {products.length > 0 ? (
                    products.map((product) => (
                        <div className="col-md-12 mb-3" key={product.productId}>
                            <ProductRow
                                id={product.productId}
                                image={product.primaryImage || 'https://via.placeholder.com/150'}
                                brand={product.brand}
                                productName={product.name}
                                productAmount={`${product.thcContent}mg THC | ${product.cbdContent}mg CBD`}
                                price={product.price}
                                salePrice={product.discountedPrice}
                            />
                        </div>
                    ))
                ) : (
                    <p>No products available</p>
                )}
            </div>
        </div>
    );
};

export default ProductList;
