import React from 'react';
import { ButtonGroup, Button } from 'react-bootstrap';

const categories = ['Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Misc'];

const CategoryFilter = ({ selectedCategory, onCategoryChange }) => {
  return (
    <div className="category-filter mb-3">
      <ButtonGroup className="d-flex overflow-auto">
        {categories.map((category) => (
          <Button
            key={category}
            variant={selectedCategory === category ? 'primary' : 'outline-secondary'}
            onClick={() => onCategoryChange(category)}
            style={{
              borderRadius: '12px',
              padding: '0.5rem 1rem',
              fontWeight: selectedCategory === category ? 'bold' : 'normal',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
            }}
          >
            <i className="bi bi-tag-fill" /> {/* Bootstrap icon */}
            {category}
          </Button>
        ))}
      </ButtonGroup>
    </div>
  );
};

export default CategoryFilter;
