import React, { useState } from 'react';
import { Form, Button, InputGroup } from 'react-bootstrap';

const MinOrderComponent = ({ initialMinOrder = 0, onUpdate }) => {
  const [minOrder, setMinOrder] = useState(initialMinOrder);

  const handleInputChange = (e) => {
    const value = e.target.value.replace(/[^0-9.]/g, ''); // Allow only numbers and decimals
    setMinOrder(value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!minOrder || isNaN(minOrder) || Number(minOrder) < 0) {
      alert('Please enter a valid minimum order amount.');
      return;
    }
    onUpdate(Number(minOrder)); // Ensure the value is sent as a number
  };

  return (
    <Form onSubmit={handleSubmit} className="p-4 border rounded shadow-sm">
      <h5 className="mb-3">Set Minimum Order</h5>
      <Form.Group className="mb-3">
        <Form.Label>Minimum Order Amount</Form.Label>
        <InputGroup>
          <InputGroup.Text>$</InputGroup.Text>
          <Form.Control
            type="text"
            placeholder="Enter minimum order amount"
            value={minOrder}
            onChange={handleInputChange}
          />
        </InputGroup>
      </Form.Group>
      <Button variant="primary" type="submit">
        Update Minimum Order
      </Button>
    </Form>
  );
};

export default MinOrderComponent;
