import React from 'react';

const DeliveryPickupToggle = ({ selectedOption, setSelectedOption }) => (
  <div className="btn-group" role="group" aria-label="Delivery or Pickup toggle">
    {/* Delivery Button */}
    <input
      type="radio"
      className="btn-check"
      name="btnradio"
      id="btnradio1"
      autoComplete="off"
      checked={selectedOption === 'Delivery'}
      onChange={() => setSelectedOption('Delivery')}
    />
    <label
      className={`btn ${selectedOption === 'Delivery' ? 'text-white' : 'text-secondary'}`}
      style={{
        backgroundColor: selectedOption === 'Delivery' ? '#1E2024' : 'white',
        border: '1px solid black',
        padding: '4px 12px',
        fontSize: '14px',
        fontFamily: 'Poppins, sans-serif',
        fontWeight: selectedOption === 'delivery' ? '600' : '400',
      }}
      htmlFor="btnradio1"
    >
      Delivery
    </label>

    {/* Pickup Button */}
    <input
      type="radio"
      className="btn-check"
      name="btnradio"
      id="btnradio3"
      autoComplete="off"
      checked={selectedOption === 'pickup'}
      onChange={() => setSelectedOption('pickup')}
    />
    <label
      className={`btn ${selectedOption === 'pickup' ? 'text-white' : 'text-secondary'}`}
      style={{
        backgroundColor: selectedOption === 'pickup' ? '#1E2024' : 'white',
        border: '1px solid black',
        padding: '4px 12px',
        fontSize: '14px',
        fontFamily: 'Poppins, sans-serif',
        fontWeight: selectedOption === 'pickup' ? '600' : '400',
      }}
      htmlFor="btnradio3"
    >
      Pickup
    </label>
  </div>
);

export default DeliveryPickupToggle;
