// File: src/components/AuthTestButton.js

import React, { useState } from 'react';
import { Button } from 'react-bootstrap';
import axios from 'axios';

const AuthTestButton = () => {
  const [loading, setLoading] = useState(false);
  const [token, setToken] = useState(null);

  const handleAuthTest = async () => {
    setLoading(true);

    const baseUrl = 'https://api.treez.io/v2.0/dispensary/partnersandbox2/config/api/gettokens';
    const clientId = 'wNzewkHXh2P5GnfCFPX3AmeA'; // Replace with your actual client_id
    const apiKey = 'MTQ5Mjg2ODdjMDBmNTliZDUxZ'; // Replace with your actual api_key

    try {
      const response = await axios.post(
        baseUrl,
        new URLSearchParams({
          client_id: clientId,
          apikey: apiKey,
        }),
        {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        }
      );

      const token = response.data.access_token;
      setToken(token);

      // Display token in popup window
      alert(`Token retrieved successfully:\n${token}`);
    } catch (error) {
      // Handle errors
      alert(`Error retrieving token:\n${error.message}`);
      console.error('Error details:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="d-flex justify-content-center mt-5">
      <Button
        variant="primary"
        onClick={handleAuthTest}
        disabled={loading}
        className="test-auth-button"
      >
        {loading ? 'Retrieving Token...' : 'Get Auth Token'}
      </Button>
      {token && (
        <div className="mt-3">
          <strong>Token:</strong> <code>{token}</code>
        </div>
      )}
    </div>
  );
};

export default AuthTestButton;
