import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    // Initialize auth state from localStorage
    const [auth, setAuth] = useState(() => {
        const storedAuth = JSON.parse(localStorage.getItem('auth'));
        return storedAuth || null;
    });
    const [loading, setLoading] = useState(!auth);

    // Function to log in the user
    const login = (authData) => {
        setAuth(authData); // Update auth state
        localStorage.setItem('auth', JSON.stringify(authData)); // Save to localStorage
    };


    // Function to log out the user
    const logout = () => {
        setAuth(null);
        localStorage.removeItem('auth');
        window.location.href = '/login'; // Redirect to login page
    };

    // Check token validity on mount or auth change
    useEffect(() => {
        const verifyToken = async () => {
            if (!auth?.token) {
                setLoading(false);
                return;
            }

            try {
                // Test token validity by making a simple authenticated request
                await axios.get('http://localhost:5001/api/auth/validate', {
                    headers: { Authorization: `Bearer ${auth.token}` },
                });
                setLoading(false); // Token is valid, stop loading
            } catch (error) {
                console.error('Invalid token:', error);
                logout(); // Log the user out if the token is invalid
            }
        };

        verifyToken();
    }, [auth]);

    return (
        <AuthContext.Provider value={{ auth, setAuth, login, logout, loading }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};

// Custom hook for accessing the AuthContext
export const useAuth = () => {
    return useContext(AuthContext);
};
