import React, { useState } from 'react';
import MapComponent from './MapComponent';

function UpdateDeliveryZoneComponent() {
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [updateStatus, setUpdateStatus] = useState(null);

  const handleLocationSelect = (location) => {
    setSelectedLocation(location);
  };

  const saveDeliveryZone = async () => {
    if (!selectedLocation || !selectedLocation.lat || !selectedLocation.lng) {
      alert('Please select a valid location before saving.');
      return;
    }

    try {
      const response = await fetch('/api/vendors/update-delivery-zone', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ deliveryZone: selectedLocation }),
      });

      const result = await response.json();
      if (response.ok) {
        setUpdateStatus('Delivery zone updated successfully!');
      } else {
        setUpdateStatus(`Failed to update delivery zone: ${result.message}`);
      }
    } catch (error) {
      console.error('Error updating delivery zone:', error);
      setUpdateStatus('An error occurred while updating the delivery zone.');
    }
  };

  return (
    <div>
      <h2>Set Delivery Zone</h2>
      <p>Click on the map to set your delivery zone location.</p>
      <MapComponent onLocationSelect={handleLocationSelect} />
      <button onClick={saveDeliveryZone} style={{ marginTop: '10px' }}>
        Save Delivery Zone
      </button>
      {updateStatus && <p>{updateStatus}</p>}
    </div>
  );
}

export default UpdateDeliveryZoneComponent;
