import React from "react";
import { BrowserRouter as Router, Route, Routes, useLocation } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import { CartProvider } from "./context/CartContext";
import CustomNavbar from "./components/CustomNavbar";
import Cart from "./components/Cart";
import AddressPage from "./pages/AddressPage";
import DeliveryPickupPage from "./pages/DeliveryPickupPage";
import CheckoutPage from "./pages/CheckoutPage";
import VendorRegistration from "./pages/VendorRegistration";
import Login from "./pages/Login";
import TestMenu from "./pages/TestMenu";
import DailyPromoForm from "./pages/DailyPromoForm";
import OrderConfirmationPage from "./pages/OrderConfirmationPage";
import VendorStorePage from "./pages/VendorStorePage";
import VendorMenuPage from "./pages/VendorMenuPage";
import TestDashboard from "./pages/TestDashboard";
import RegistrationPage from "./pages/RegistrationPage"; // Import RegistrationPage

function App() {
    const location = useLocation(); // Get the current location

    // Define routes where CustomNavbar should not appear
    const hideNavbarRoutes = ["/register"];

    const shouldShowNavbar = !hideNavbarRoutes.includes(location.pathname);

    return (
        <AuthProvider>
            <CartProvider>
                <Router>
                    {shouldShowNavbar && <CustomNavbar />} {/* Conditionally render the navbar */}
                    <Cart />
                    <Routes>
                        {/* Public Routes */}
                        <Route path="/" element={<AddressPage />} />
                        <Route path="/nearby" element={<DeliveryPickupPage />} />
                        <Route path="/checkout" element={<CheckoutPage />} />
                        <Route path="/register" element={<RegistrationPage />} /> {/* Use RegistrationPage here */}
                        <Route path="/login" element={<Login />} />
                        <Route path="/menu" element={<TestMenu />} />
                        <Route path="/test" element={<DailyPromoForm />} />
                        <Route path="/order-confirmation/:orderId" element={<OrderConfirmationPage />} />
                        <Route path="/vendor/:vendorId" element={<VendorStorePage />} />
                        <Route path="/vendor/:vendorId/menu" element={<VendorMenuPage />} />

                        {/* Protected Routes */}
                        <Route path="/vendor-dashboard" element={<TestDashboard />} />
                    </Routes>
                </Router>
            </CartProvider>
        </AuthProvider>
    );
}

export default App;
