import React, { useState } from "react";
import axios from "axios";

const VendorRegistration = () => {
  const [step, setStep] = useState(1); // Track current step
  const [formData, setFormData] = useState({
    dispensaryType: "",
    licenseState: "",
    licenseNumber: "",
    businessAddress: {
      street: "",
      city: "",
      state: "",
      zip: "",
    },
    dispensaryName: "",
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    termsAccepted: false,
  });

  const [errors, setErrors] = useState({});

  // Move to the next or previous step
  const nextStep = () => setStep((prev) => prev + 1);
  const prevStep = () => setStep((prev) => prev - 1);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleNestedChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  // Validate Step 2 Fields
  const validateStep2 = () => {
    const newErrors = {};
    if (!formData.licenseState) newErrors.licenseState = "License state is required";
    if (!formData.licenseNumber) newErrors.licenseNumber = "License number is required";
    if (!formData.businessAddress.street) newErrors.street = "Street is required";
    if (!formData.businessAddress.city) newErrors.city = "City is required";
    if (!formData.businessAddress.state) newErrors.state = "State is required";
    if (!formData.businessAddress.zip) newErrors.zip = "ZIP is required";
    if (!formData.firstName) newErrors.firstName = "First name is required";
    if (!formData.lastName) newErrors.lastName = "Last name is required";
    if (!/^\d{10}$/.test(formData.contactNumber)) newErrors.contactNumber = "Invalid phone number";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) newErrors.email = "Invalid email address";
    if (formData.password.length < 8)
      newErrors.password = "Password must be at least 8 characters long";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Submit Registration
  const handleSubmit = async () => {
    try {
      // Convert business address to geolocation using an API (e.g., Google Maps Geocoding API)
      const { data: geoData } = await axios.post("/api/geocode", {
        address: `${formData.businessAddress.street}, ${formData.businessAddress.city}, ${formData.businessAddress.state}, ${formData.businessAddress.zip}`,
      });

      const payload = {
        ...formData,
        businessAddress: {
          ...formData.businessAddress,
          geolocation: {
            type: "Point",
            coordinates: geoData.coordinates,
          },
        },
      };

      const response = await axios.post("/api/vendors/register", payload);
      alert("Registration Successful!");
      window.location.href = "/vendor/dashboard"; // Redirect to vendor dashboard
    } catch (error) {
      console.error(error);
      alert("Error during registration. Please try again.");
    }
  };

  return (
    <div className="container mt-5">



      {step === 1 && (
        <div>
          <h2>What services do you provide?</h2>
          <div className="d-flex justify-content-between">
            <button
              className={`btn btn-outline-primary ${
                formData.dispensaryType === "storefront" ? "active" : ""
              }`}
              onClick={() => {
                setFormData((prev) => ({ ...prev, dispensaryType: "storefront" }));
                nextStep();
              }}
            >
              <i className="bi bi-shop"></i> Pickup
            </button>
            <button
              className={`btn btn-outline-primary ${
                formData.dispensaryType === "delivery" ? "active" : ""
              }`}
              onClick={() => {
                setFormData((prev) => ({ ...prev, dispensaryType: "delivery" }));
                nextStep();
              }}
            >
              <i className="bi bi-truck"></i> Delivery
            </button>
            <button
              className={`btn btn-outline-primary ${
                formData.dispensaryType === "both" ? "active" : ""
              }`}
              onClick={() => {
                setFormData((prev) => ({ ...prev, dispensaryType: "both" }));
                nextStep();
              }}
            >
              <i className="bi bi-geo-alt"></i> Pickup & Delivery
            </button>
          </div>
        </div>
      )}

      {step === 2 && (
        <div>
          <h2>Dispensary Info</h2>
          <div>
            <label>License State</label>
            <select
              name="licenseState"
              className="form-control"
              value={formData.licenseState}
              onChange={handleChange}
            >
              <option value="">Select State</option>
              <option value="CA">CA</option>
              <option value="NV">NV</option>
              {/* Add all states */}
            </select>
            {errors.licenseState && <small className="text-danger">{errors.licenseState}</small>}
          </div>

          <div>
            <label>License Number</label>
            <input
              name="licenseNumber"
              className="form-control"
              value={formData.licenseNumber}
              onChange={handleChange}
            />
            {errors.licenseNumber && <small className="text-danger">{errors.licenseNumber}</small>}
          </div>

          <h3>Business Address</h3>
          {/* Address Fields */}
          <button className="btn btn-secondary" onClick={prevStep}>
            Go Back
          </button>
          <button
            className="btn btn-primary"
            onClick={() => {
              if (validateStep2()) nextStep();
            }}
          >
            Continue
          </button>
        </div>
      )}

      {step === 3 && (
        <div>
          <h2>Terms & Conditions</h2>
          <div
            className="border p-3 mb-3"
            style={{ height: "200px", overflowY: "scroll" }}
          >
            <p>
              By registering as a vendor on WeedHurry, you agree that WeedHurry
              is not responsible for any illegal actions performed by vendors...
              {/* Include detailed terms */}
            </p>
          </div>
          <div className="form-check">
            <input
              type="checkbox"
              className="form-check-input"
              checked={formData.termsAccepted}
              onChange={(e) =>
                setFormData((prev) => ({ ...prev, termsAccepted: e.target.checked }))
              }
            />
            <label className="form-check-label">I agree to the terms & conditions</label>
          </div>
          <button className="btn btn-secondary" onClick={prevStep}>
            Go Back
          </button>
          <button
            className="btn btn-primary"
            onClick={() => {
              if (formData.termsAccepted) handleSubmit();
              else alert("You must agree to the terms to complete registration.");
            }}
          >
            Complete Registration
          </button>
        </div>
      )}
    </div>
  );
};

export default VendorRegistration;
