import React, { useState, useEffect, useCallback } from 'react';
import { Tabs, Tab, Button, Col, Row, Accordion } from 'react-bootstrap';
import RecentOrders from '../components/RecentOrders';
import InventoryComponent from '../components/InventoryComponent';
import LogoutButton from '../components/LogoutButton';
import UpdateDeliveryZoneComponent from '../components/UpdateDeliveryZoneComponent';
import BusinessHoursComponent from '../components/BusinessHoursComponent';
import MinOrderComponent from '../components/MinOrderComponent';
import { useLoadScript } from '@react-google-maps/api';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import VendorLogoCard from '../components/VendorLogoCard';
import VendorInfoCard from '../components/VendorInfoCard';
import DeactivateAccount from '../components/DeactivateAccount';
import DailyPromoForm from './DailyPromoForm';
import DashNavBar from '../components/DashNavBar';

const libraries = ['places'];

const defaultHours = {
  monday: '',
  tuesday: '',
  wednesday: '',
  thursday: '',
  friday: '',
  saturday: '',
  sunday: '',
};

const generateTimeSlots = () => {
  const slots = [];
  let time = 6 * 60;
  const end = 24 * 60;

  while (time < end) {
    const hours = Math.floor(time / 60);
    const label = `${hours % 12 || 12}:00 ${hours < 12 ? 'AM' : 'PM'}`;
    slots.push(label);
    time += 60;
  }
  return slots;
};

const VendorDashboard = () => {
  const { logout } = useAuth();
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });
  const navigate = useNavigate();

  const [initialNotice, setInitialNotice] = useState('');
  const [initialMinOrder, setInitialMinOrder] = useState(0);
  const [initialHours, setInitialHours] = useState(defaultHours);
  const [showSettings, setShowSettings] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [initialAcceptedPayments, setInitialAcceptedPayments] = useState([]);

  const handleExpiredToken = () => {
    console.warn('Session expired. Logging out.');
    logout();
    navigate('/login');
  };

  const fetchVendorData = useCallback(async () => {
    if (loading) return;
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:5001/api/vendors/profile', {
        withCredentials: true,
      });

      const { data } = response.data;
      setInitialMinOrder(data.minOrder || 0);
      setInitialAcceptedPayments(data.acceptedPayments || []);
      setInitialHours(data.businessHours || defaultHours);

      setError(null);
    } catch (error) {
      console.error('Error fetching vendor data:', error.message);

      if (error.response?.status === 401) {
        handleExpiredToken();
      } else {
        setError('Failed to fetch vendor data.');
      }
    } finally {
      setLoading(false);
    }
  }, [handleExpiredToken]);


  const handleUpdateSettings = async (formData) => {
    try {
      const response = await axios.put(
        'http://localhost:5001/api/vendors/min-order',
        {
          minOrder: formData.minOrder,
          acceptedPayments: formData.acceptedPayments
        },
        { withCredentials: true }
      );

      if (response.data.success) {
        alert('Settings updated successfully!');
        setInitialMinOrder(formData.minOrder);
        setInitialAcceptedPayments(formData.acceptedPayments);
      } else {
        alert(response.data.message || 'Failed to update settings.');
      }
    } catch (error) {
      console.error('Error updating settings:', error);
      alert('An error occurred while updating the settings. Please try again.');
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading map...</div>;

  return (
    <div style={{ paddingTop: '60px' }}> {/* Add padding to account for fixed navbar */}
      <DashNavBar />
      {loading && <div>Loading...</div>}
      {error && <div className="alert alert-danger">{error}</div>}
      <Tabs defaultActiveKey="orders" id="vendor-dashboard-tabs" className="mb-3">
        <Tab eventKey="orders" title="Orders">
          <RecentOrders />
        </Tab>
        <Tab eventKey="inventory" title="Inventory">
          <InventoryComponent />
        </Tab>
        <Tab eventKey="profile" title="Profile">
  <div className="p-0">
    <Accordion defaultActiveKey="0">
      {/* Delivery Zone Section */}
      <Accordion.Item eventKey="0">
        <Accordion.Header>Delivery Zone</Accordion.Header>
        <Accordion.Body>
          <Row className="g-4">
            <UpdateDeliveryZoneComponent isLoaded={isLoaded} loadError={loadError} />
          </Row>
        </Accordion.Body>
      </Accordion.Item>

      {/* Business Hours Section */}
      <Accordion.Item eventKey="1">
        <Accordion.Header>Business Hours</Accordion.Header>
        <Accordion.Body>
          <BusinessHoursComponent
            initialHours={initialHours}
            onUpdate={async (updatedHours) => {
              try {
                const response = await axios.put(
                  'http://localhost:5001/api/vendors/business-hours',
                  { businessHours: updatedHours },
                  { withCredentials: true }
                );

                if (response.data.success) {
                  alert('Business hours updated successfully!');
                  setInitialHours(updatedHours);
                } else {
                  alert(response.data.message || 'Failed to update business hours.');
                }
              } catch (error) {
                console.error('Error updating business hours:', error);
                alert('An error occurred while updating business hours. Please try again.');
              }
            }}
            availableTimes={generateTimeSlots()}
          />
        </Accordion.Body>
      </Accordion.Item>

      {/* Daily Promo Section */}
      <Accordion.Item eventKey="2">
        <Accordion.Header>Daily Promotion</Accordion.Header>
        <Accordion.Body>
          <DailyPromoForm />
        </Accordion.Body>
      </Accordion.Item>

      {/* Minimum Order Section */}
      <Accordion.Item eventKey="3">
        <Accordion.Header>Minimum Order</Accordion.Header>
        <Accordion.Body>
          <Col xs={12}>
            <MinOrderComponent
              minOrder={initialMinOrder}
              onUpdate={handleUpdateMinOrder}
            />
          </Col>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  </div>
</Tab>
      </Tabs>
    </div>
  );
};

export default VendorDashboard;
