import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Import the AuthContext hook

const AddProductPage = () => {
  const { auth } = useAuth(); // Access vendor details from the context
  const [brand, setBrand] = useState('');
  const [category, setCategory] = useState('Flower'); // Default category
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedAmount, setSelectedAmount] = useState('');
  const [availableStrains, setAvailableStrains] = useState([]);
  const [price, setPrice] = useState('');
  const [salePrice, setSalePrice] = useState('');
  const [stock, setStock] = useState('');
  const [selectedImage, setSelectedImage] = useState('');
  const [weight, setWeight] = useState('');
  const [thcContent, setThcContent] = useState('');

  // Fetch products by brand and category
  useEffect(() => {
    if (brand) {
      const queryParams = new URLSearchParams({ brand, category }).toString();
      fetch(`/api/products/filters?${queryParams}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand, category]);

  const handleProductSelect = (product) => {
    setSelectedProduct(product);
    setSelectedImage(product.images?.[0] || '');
    setBrand(product.brand);
    setSelectedAmount('');
    setAvailableStrains([]);
  };

  const handleAmountSelect = (amount) => {
    setSelectedAmount(amount);
    if (selectedProduct) {
      const strains = selectedProduct.variations
        .filter((variation) => variation.amount === amount)
        .map((variation) => variation.strain);
      setAvailableStrains(strains);
    }
  };

  const handleAddToInventory = async () => {
    if (!selectedProduct || !selectedAmount || !price || !stock) {
      alert('Please fill out all required fields.');
      return;
    }

    try {
      const response = await fetch('/api/vendor/inventory', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendorId: auth.vendorId,
          productId: selectedProduct._id,
          price: parseFloat(price),
          salePrice: salePrice ? parseFloat(salePrice) : undefined,
          stock: parseInt(stock, 10),
          weight,
          variation: {
            amount: selectedAmount,
            strain: availableStrains.length === 1 ? availableStrains[0] : undefined, // Select strain if only one is available
          },
          selectedImage,
        }),
      });

      const data = await response.json();
      if (response.ok) {
        alert(data.message);
        // Reset fields
        setBrand('');
        setCategory('Flower');
        setSelectedProduct(null);
        setSelectedAmount('');
        setAvailableStrains([]);
        setPrice('');
        setSalePrice('');
        setStock('');
        setWeight('');
        setThcContent('');
        setSelectedImage('');
      } else {
        alert(data.message || 'Failed to add product to inventory.');
      }
    } catch (error) {
      console.error('Error adding product to inventory:', error);
      alert('An error occurred while adding the product.');
    }
  };

  return (
    <div className="container mt-4">
      <h1>Add Product to Inventory</h1>
      <div className="mb-3">
        <label className="form-label">Category</label>
        <select
          className="form-select"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="Flower">Flower</option>
          <option value="Pre-roll">Pre-roll</option>
          <option value="Vape">Vape</option>
          <option value="Edible">Edible</option>
          <option value="Extract">Extract</option>
          <option value="Topical">Topical</option>
          <option value="Gear">Gear</option>
        </select>
      </div>

      <div className="mb-3">
        <label className="form-label">Brand</label>
        <input
          type="text"
          className="form-control"
          placeholder="Enter brand name"
          value={brand}
          onChange={(e) => setBrand(e.target.value)}
        />
      </div>

      {selectedProduct ? (
        <div className="mb-3">
          <div className="list-group">
            <div className="list-group-item active">
              <strong>{selectedProduct.brand}</strong> - {selectedProduct.name}
            </div>
          </div>
        </div>
      ) : (
        products.length > 0 && (
          <div className="mb-3">
            <label className="form-label">Matching Products</label>
            <ul className="list-group">
              {products.map((product) => (
                <li
                  key={product._id}
                  className="list-group-item"
                  onClick={() => handleProductSelect(product)}
                  style={{ cursor: 'pointer' }}
                >
                  <strong>{product.brand}</strong> - {product.name}{' '}
                  {product.images?.[0] && (
                    <img
                      src={product.images[0]}
                      alt={product.name}
                      style={{ width: '40px', marginLeft: '10px' }}
                    />
                  )}
                </li>
              ))}
            </ul>
          </div>
        )
      )}

      {selectedProduct && (
        <>
          <div className="mb-3">
            <label className="form-label">Select Variation Amount</label>
            <select
              className="form-select"
              value={selectedAmount}
              onChange={(e) => handleAmountSelect(e.target.value)}
            >
              <option value="" disabled>
                Select amount
              </option>
              {selectedProduct.variations.map((variation, index) => (
                <option key={index} value={variation.amount}>
                  {variation.amount}
                </option>
              ))}
            </select>
          </div>

          {availableStrains.length > 0 && (
            <div className="mb-3">
              <label className="form-label">Select Strain</label>
              <select className="form-select">
                {availableStrains.map((strain, index) => (
                  <option key={index} value={strain}>
                    {strain}
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Remaining Inputs */}
          <div className="mb-3">
            <label className="form-label">Price</label>
            <input
              type="number"
              className="form-control"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter price"
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Stock</label>
            <input
              type="number"
              className="form-control"
              value={stock}
              onChange={(e) => setStock(e.target.value)}
              placeholder="Enter stock"
            />
          </div>

          <button className="btn btn-success" onClick={handleAddToInventory}>
            Add to Inventory
          </button>
        </>
      )}
    </div>
  );
};

export default AddProductPage;
