import React, { useEffect, useState } from 'react';
import ChangeAddressLink from './ChangeAddressLink';
import VendorCard from './VendorCard';

const NearbyVendorsPage = () => {
  const [vendors, setVendors] = useState([]);
  const [address, setAddress] = useState(localStorage.getItem('userAddress') || '');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchVendors = async () => {
      if (!address) {
        setError('Please provide a valid address.');
        return;
      }

      try {
        // Geocoding the address
        const geocodeResponse = await fetch(`/api/geocode?address=${encodeURIComponent(address)}`);
        const geocodeData = await geocodeResponse.json();

        if (geocodeData.status !== 'OK') {
          console.error('Error geocoding address:', geocodeData.error_message);
          setError('Could not fetch location for the given address.');
          return;
        }

        const { lat, lng } = geocodeData.location;

        // Fetching nearby vendors
        const vendorsResponse = await fetch(`/api/vendors/nearby?lat=${lat}&lng=${lng}`);
        const vendorsData = await vendorsResponse.json();

        if (vendorsData.error || !vendorsData.vendors) {
          setError('No vendors found near your location.');
          return;
        }

        setVendors(vendorsData.vendors);
      } catch (err) {
        console.error('Error fetching vendors:', err);
        setError('Failed to load vendors. Please try again later.');
      }
    };

    fetchVendors();
  }, [address]);


  if (!address) {
    return <p>No address set. Please go back and enter your address.</p>;
  }

  return (
    <div className="container mt-4">
      <h1>Vendors Near You</h1>
      <ChangeAddressLink />
      {error ? (
        <p className="text-danger">{error}</p>
      ) : vendors.length ? (
        <div className="vendor-list">
          {vendors.map((vendor) => (
            <VendorCard key={vendor._id} vendor={vendor} />
          ))}
        </div>
      ) : (
        <p>No vendors found near your address.</p>
      )}
    </div>
  );
};

export default NearbyVendorsPage;
