import React, { useEffect, useRef } from 'react';
import { Form } from 'react-bootstrap';

const AddressInput = ({ onAddressSelect }) => {
  const inputRef = useRef(null);

  useEffect(() => {
    // Load Google Places Autocomplete
    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['geocode'], // Restrict results to addresses
      componentRestrictions: { country: 'us' }, // Adjust to your region if needed
    });

    // Listen for place selection
    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place.geometry) {
        const location = {
          lat: place.geometry.location.lat(),
          lng: place.geometry.location.lng(),
          address: place.formatted_address,
        };
        onAddressSelect(location); // Pass selected location to parent
      }
    });
  }, [onAddressSelect]);

  return (
    <Form.Group controlId="addressInput">
      <Form.Control
        ref={inputRef}
        type="text"
        placeholder="Enter your address"
        className="mb-3"
      />
    </Form.Group>
  );
};

export default AddressInput;
