import React, { useState, useRef } from 'react';
import { Offcanvas, Form, Button, ListGroup } from 'react-bootstrap';

const AddressInput = ({ onAddressUpdate }) => {
  const [show, setShow] = useState(false);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const inputRef = useRef(null);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleAddressSelect = (place) => {
    if (!place.geometry) return;

    const location = {
      lat: place.geometry.location.lat(),
      lng: place.geometry.location.lng(),
      address: place.formatted_address,
    };

    setSelectedAddress(location.address);
    onAddressUpdate({ lat: location.lat, lng: location.lng });
    handleClose(); // Close the slide-in window
  };

  const initializeGoogleAutocomplete = () => {
    if (!window.google || !inputRef.current) return;

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['geocode'],
      componentRestrictions: { country: 'us' },
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      handleAddressSelect(place);
    });
  };

  return (
    <>
      {/* Main Display Under Navbar */}
      <div className="address-bar d-flex align-items-center">
        <i className="bi bi-geo-alt-fill me-2"></i>
        <span className="address-link" onClick={handleShow} style={{ textDecoration: 'underline', cursor: 'pointer' }}>
          {selectedAddress ? `Deliver to ${selectedAddress}` : 'Enter your address'}
        </span>
      </div>

      {/* Slide-In Window */}
      <Offcanvas show={show} onHide={handleClose} placement="start">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Enter your address</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <Form.Group controlId="addressInput">
            <Form.Control
              ref={inputRef}
              type="text"
              placeholder="Enter your address"
              onFocus={initializeGoogleAutocomplete}
            />
          </Form.Group>

          {/* Address Dropdown Suggestions */}
          <ListGroup className="mt-3">
            {/* Mocked Dropdown for UI Example */}
            {/* This will be replaced with actual Google Autocomplete Suggestions */}
            <ListGroup.Item action>
              <div className="d-flex justify-content-between align-items-center">
                <span>123 Main St, Los Angeles, CA</span>
                <i className="bi bi-chevron-right"></i>
              </div>
            </ListGroup.Item>
            <ListGroup.Item action>
              <div className="d-flex justify-content-between align-items-center">
                <span>456 Elm St, Santa Monica, CA</span>
                <i className="bi bi-chevron-right"></i>
              </div>
            </ListGroup.Item>
          </ListGroup>

          <Button variant="secondary" onClick={handleClose} className="mt-3">
            Cancel
          </Button>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
};

export default AddressInput;
