import React, { useState } from 'react';
import { Navbar, Container, Image } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const DashNavBar = () => {
  const [showSidebar, setShowSidebar] = useState(false);
  const [showEditSidebar, setShowEditSidebar] = useState(false);
  const [vendorData, setVendorData] = useState(null);
  const navigate = useNavigate();

  // Fetch vendor data on component mount
  React.useEffect(() => {
    const fetchVendorData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/vendors/profile', {
          withCredentials: true,
        });
        setVendorData(response.data.data);
      } catch (error) {
        console.error('Error fetching vendor data:', error);
      }
    };
    fetchVendorData();
  }, []);

  const handleClose = () => {
    setShowSidebar(false);
    setShowEditSidebar(false);
  };

  const handleBack = () => {
    setShowEditSidebar(false);
  };

  const handleLogoClick = () => {
    if (vendorData?._id) {
      navigate(`/vendor/${vendorData._id}/menu`);
    }
  };

  // Style objects
  const navbarStyle = {
    backgroundColor: '#1a1a1a',
    height: '60px',
  };

  const logoStyle = {
    height: '35px',
    width: 'auto',
    borderRadius: '6px',
    cursor: 'pointer'
  };

  const vendorLogoStyle = {
    height: '40px',
    width: '40px',
    borderRadius: '50%',
    cursor: 'pointer',
    objectFit: 'cover',
  };

  const dashboardTextStyle = {
    color: '#ffffff',
    fontSize: '0.9rem',
    margin: '0',
    fontWeight: '400',
    position: 'absolute',
    left: '50%',
    transform: 'translateX(-50%)',
  };

  const sidebarStyle = {
    position: 'fixed',
    right: showSidebar ? '0' : '-320px',
    top: '0',
    width: '320px',
    height: '100vh',
    backgroundColor: 'white',
    boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.2)',
    transition: 'right 0.3s ease-in-out',
    zIndex: 1040,
  };

  const editSidebarStyle = {
    ...sidebarStyle,
    right: showEditSidebar ? '0' : '-320px',
    zIndex: 1041,
  };

  const overlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: (showEditSidebar ? 1040 : 1039),
    display: (showSidebar || showEditSidebar) ? 'block' : 'none',
  };

  return (
    <>
      <Navbar style={navbarStyle} variant="dark" fixed="top">
        <Container fluid className="position-relative">
          <Navbar.Brand>
            <Image
              src={require('../assets/images/miniLogo.jpg')}
              alt="Mini Logo"
              style={logoStyle}
              onClick={handleLogoClick}
            />
          </Navbar.Brand>
          <span style={dashboardTextStyle}>Dashboard</span>
          <Image
            src={vendorData?.logoUrl || '/placeholder-profile.png'}
            alt="Vendor Logo"
            style={vendorLogoStyle}
            onClick={() => setShowSidebar(true)}
          />
        </Container>
      </Navbar>

      {/* Overlay */}
      <div style={overlayStyle} onClick={handleClose} />

      {/* Main Sidebar */}
      <div style={sidebarStyle}>
        {showSidebar && (
          <div className="p-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h5 className="mb-0">Profile</h5>
              <button
                className="btn btn-link text-dark"
                onClick={handleClose}
              >
                ×
              </button>
            </div>
            {vendorData && (
              <>
                <h4 className="fw-bold mb-3">{vendorData.dispensaryName}</h4>
                <p className="mb-1">{`${vendorData.firstName} ${vendorData.lastName}`}</p>
                <p className="mb-1">{vendorData.contactNumber}</p>
                <p className="mb-3">{vendorData.email}</p>
                <button
                  className="btn btn-link p-0"
                  onClick={() => setShowEditSidebar(true)}
                >
                  Edit
                </button>
              </>
            )}
          </div>
        )}
      </div>

      {/* Edit Sidebar */}
      <div style={editSidebarStyle}>
        {showEditSidebar && (
          <div className="p-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <button
                className="btn btn-link text-dark"
                onClick={handleBack}
              >
                ←
              </button>
              <h5 className="mb-0">Edit Profile</h5>
              <button
                className="btn btn-link text-dark"
                onClick={handleClose}
              >
                ×
              </button>
            </div>
            {/* Edit form will go here */}
          </div>
        )}
      </div>
    </>
  );
};

export default DashNavBar;
